style:
:root {
	--cyber-blue: #00f3ff;
	--cyber-pink: #ff00ff;
	--cyber-green: #00ff9d;
	--cyber-orange: #ff6b00;
	--bg: #000814;
}

body {
	font-family: "Orbitron", sans-serif;
	min-height: 100vh;
	display: flex;
	justify-content: center;
	align-items: center;
	background: var(--bg);
	margin: 0;
	overflow: hidden;
}

.holo-container {
	position: fixed;
	top: 20px;
	right: 20px;
	display: flex;
	flex-direction: column;
	gap: 15px;
	z-index: 1000;
}

.holo-alert {
	position: relative;
	padding: 20px;
	border-radius: 8px;
	background: rgba(0, 8, 20, 0.9);
	color: white;
	border: 1px solid;
	transform: translateX(150%);
	animation: holoSlide 0.5s forwards;
	backdrop-filter: blur(10px);
	box-shadow: 0 0 20px rgba(0, 243, 255, 0.2);
	max-width: 300px;
	cursor: pointer;
}

@keyframes holoSlide {
	to {
		transform: translateX(0);
	}
}

.holo-alert::before {
	content: "";
	position: absolute;
	inset: 0;
	border-radius: 8px;
	padding: 2px;
	background: linear-gradient(45deg, var(--cyber-blue), var(--cyber-pink), transparent);
	-webkit-mask: linear-gradient(#000 0 0) content-box, linear-gradient(#000 0 0);
	mask: linear-gradient(#000 0 0) content-box, linear-gradient(#000 0 0);
	-webkit-mask-composite: xor;
	mask-composite: exclude;
	animation: holoGlow 2s infinite linear;
}

@keyframes holoGlow {
	0% {
		opacity: 0.8;
	}
	50% {
		opacity: 1;
	}
	100% {
		opacity: 0.8;
	}
}

.alert-icon {
	font-size: 1.5em;
	margin-bottom: 10px;
}

.alert-content {
	line-height: 1.4;
}

.close-btn {
	position: absolute;
	top: 10px;
	right: 10px;
	cursor: pointer;
	opacity: 0.7;
	transition: opacity 0.2s;
}

.close-btn:hover {
	opacity: 1;
}

.sound-wave {
	position: absolute;
	bottom: -5px;
	left: 0;
	width: 100%;
	height: 10px;
	background: repeating-linear-gradient(
		90deg,
		transparent,
		transparent 3px,
		var(--cyber-blue) 3px,
		var(--cyber-blue) 6px
	);
	animation: wave 0.5s infinite linear;
}

@keyframes wave {
	from {
		background-position: 0 0;
	}
	to {
		background-position: 12px 0;
	}
}

.control-panel {
	position: fixed;
	bottom: 20px;
	display: grid;
	grid-template-columns: repeat(2, 1fr);
	gap: 15px;
	padding: 20px;
	background: rgba(0, 8, 20, 0.8);
	border-radius: 8px;
	border: 1px solid var(--cyber-blue);
}

.cyber-btn {
	padding: 12px 24px;
	background: transparent;
	border: 1px solid var(--cyber-blue);
	color: var(--cyber-blue);
	cursor: pointer;
	position: relative;
	overflow: hidden;
	transition: 0.3s;
}

.cyber-btn::before {
	content: "";
	position: absolute;
	top: 0;
	left: -100%;
	width: 100%;
	height: 100%;
	background: linear-gradient(90deg, transparent, rgba(0, 243, 255, 0.2), transparent);
	transition: 0.5s;
}

.cyber-btn:hover {
	box-shadow: 0 0 10px var(--cyber-blue);
}

.cyber-btn:hover::before {
	left: 100%;
}

script:
const alertData = {
	info: {
		icon: "fa-terminal",
		message: "System update available (v2.3.5)",
		color: "var(--cyber-blue)",
	},
	alert: {
		icon: "fa-shield-halved",
		message: "Security breach detected!",
		color: "var(--cyber-pink)",
	},
	success: {
		icon: "fa-rocket",
		message: "Launch sequence complete",
		color: "var(--cyber-green)",
	},
	error: {
		icon: "fa-skull-crossbones",
		message: "Critical system failure!",
		color: "var(--cyber-orange)",
	},
};

function createHoloAlert(type) {
	const container = document.querySelector(".holo-container");
	const alert = document.createElement("div");
	const data = alertData[type];

	alert.className = "holo-alert";
	alert.innerHTML = `
		<i class="fas ${data.icon} alert-icon"></i>
		<div class="alert-content">${data.message}</div>
		<div class="sound-wave"></div>
		<div class="close-btn" onclick="this.parentElement.remove()">
			<i class="fas fa-times"></i>
		</div>
	`;

	alert.style.borderColor = data.color;
	alert.querySelector(
		".sound-wave",
	).style.background = `repeating-linear-gradient(90deg, transparent, transparent 3px, ${data.color} 3px, ${data.color} 6px)`;

	container.prepend(alert);

	// Add sound effect
	const audio = new Audio("data:audio/wav;base64,UklGRl9vT19XQVZFZm10IBAAAAABAAEAQB8AAEAfAAABAAgAZGF0YU");
	audio.play();

	setTimeout(() => {
		alert.style.opacity = "0";
		setTimeout(() => alert.remove(), 500);
	}, 5000);
}
