style:
:root {
	--primary-spacing: 20px;
	--border-radius: 8px;
	--shadow: 0 4px 6px rgba(0, 0, 0, 0.1);

	/* Light Theme */
	--bg-color: #ffffff;
	--text-color: #1a1a1a;
	--success-bg: #f0fdf4;
	--success-text: #15803d;
	--info-bg: #f0f9ff;
	--info-text: #1d4ed8;
	--warning-bg: #fefce8;
	--warning-text: #a16207;
	--error-bg: #fef2f2;
	--error-text: #b91c1c;
	--neutral-bg: #f8fafc;
	--neutral-text: #64748b;
}

body.dark-mode {
	--bg-color: #1e293b;
	--text-color: #f8fafc;
	--success-bg: #14532d;
	--success-text: #4ade80;
	--info-bg: #1e3a8a;
	--info-text: #60a5fa;
	--warning-bg: #854d0e;
	--warning-text: #fde047;
	--error-bg: #7f1d1d;
	--error-text: #fca5a5;
	--neutral-bg: #334155;
	--neutral-text: #94a3b8;
}

body {
	font-family: "Segoe UI", system-ui, sans-serif;
	margin: 0;
	padding: 20px;
	background: var(--bg-color);
	color: var(--text-color);
	transition: background 0.3s ease;
}

.toast-container {
	position: fixed;
	top: var(--primary-spacing);
	right: var(--primary-spacing);
	display: flex;
	flex-direction: column;
	gap: 12px;
	max-width: 400px;
	width: 100%;
	z-index: 9999;
}

.toast {
	padding: 16px;
	border-radius: var(--border-radius);
	background: var(--neutral-bg);
	color: var(--neutral-text);
	display: flex;
	align-items: flex-start;
	gap: 14px;
	box-shadow: var(--shadow);
	animation: slideIn 0.3s cubic-bezier(0.25, 0.46, 0.45, 0.94);
	transition: transform 0.2s, opacity 0.2s;
}

.toast:hover {
	transform: translateX(-4px);
}

.toast-icon {
	font-size: 1.4rem;
	margin-top: 2px;
}

.toast-content {
	flex-grow: 1;
}

.toast-title {
	font-weight: 600;
	margin-bottom: 4px;
	font-size: 0.95rem;
}

.toast-message {
	margin: 0;
	font-size: 0.875rem;
	line-height: 1.4;
	opacity: 0.9;
}

.toast-close {
	background: none;
	border: none;
	color: inherit;
	cursor: pointer;
	opacity: 0.7;
	padding: 4px;
	margin-left: 8px;
	transition: opacity 0.2s;
}

.toast-close:hover {
	opacity: 1;
}

.toast-progress {
	position: absolute;
	bottom: 0;
	left: 0;
	width: 100%;
	height: 3px;
	background: rgba(0, 0, 0, 0.1);
}

.toast-progress::after {
	content: "";
	position: absolute;
	top: 0;
	left: 0;
	width: 100%;
	height: 100%;
	background: currentColor;
	animation: progress 10s linear forwards;
}

/* Toast Variants */

.toast.success {
	background: var(--success-bg);
	color: var(--success-text);
}

.toast.info {
	background: var(--info-bg);
	color: var(--info-text);
}

.toast.warning {
	background: var(--warning-bg);
	color: var(--warning-text);
}

.toast.error {
	background: var(--error-bg);
	color: var(--error-text);
}

/* Animations */
@keyframes slideIn {
	from {
		transform: translateX(100%);
	}
	to {
		transform: translateX(0);
	}
}

@keyframes progress {
	from {
		width: 100%;
	}
	to {
		width: 0%;
	}
}

/* Dark Mode Toggle */
.theme-toggle {
	position: fixed;
	bottom: var(--primary-spacing);
	left: var(--primary-spacing);
	padding: 12px 20px;
	border-radius: var(--border-radius);
	background: var(--neutral-bg);
	color: inherit;
	border: none;
	cursor: pointer;
	display: flex;
	align-items: center;
	gap: 8px;
	box-shadow: var(--shadow);
}

script:
function closeToast(event) {
	const toast = event.target.closest(".toast");
	toast.style.transform = "translateX(150%)";
	toast.style.opacity = "0";
	setTimeout(() => toast.remove(), 300);
}

// Auto-remove toasts
document.querySelectorAll(".toast").forEach((toast) => {
	setTimeout(() => {
		toast.style.transform = "translateX(150%)";
		toast.style.opacity = "0";
		setTimeout(() => toast.remove(), 300);
	}, 10000);
});
