style:
:root {
	--bg-color: #ffffff;
	--text-color: #333333;
	--accent-color: #3b82f6;
	--hover-bg: #f0f4ff;
	--shadow: 0 4px 6px rgba(0, 0, 0, 0.05);
}

.dark-theme {
	--bg-color: #1a1a1a;
	--text-color: #e5e7eb;
	--accent-color: #60a5fa;
	--hover-bg: rgba(96, 165, 250, 0.1);
	--shadow: 0 4px 6px rgba(0, 0, 0, 0.2);
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Oxygen, Ubuntu, Cantarell, sans-serif;
}

body {
	min-height: 100vh;
	display: flex;
	align-items: center;
	justify-content: center;
	background-color: var(--bg-color);
	color: var(--text-color);
	transition: background-color 0.3s ease;
	padding: 1rem;
}

.breadcrumbs {
	background: var(--bg-color);
	padding: 1rem 1.5rem;
	border-radius: 12px;
	box-shadow: var(--shadow);
	width: 100%;
	max-width: 1200px;
	position: relative;
}

.breadcrumbs-list {
	display: flex;
	align-items: center;
	gap: 0.5rem;
	list-style: none;
	flex-wrap: wrap;
}

.breadcrumbs-item {
	display: flex;
	align-items: center;
	gap: 0.5rem;
	color: var(--text-color);
	opacity: 0.9;
	font-size: 0.9rem;
}

.breadcrumbs-item:not(:last-child)::after {
	content: "â€º";
	color: var(--text-color);
	opacity: 0.6;
	font-size: 1.2rem;
	margin-left: 0.5rem;
}

.breadcrumbs-link {
	text-decoration: none;
	color: inherit;
	transition: all 0.2s ease;
	padding: 0.25rem 0.5rem;
	border-radius: 6px;
}

.breadcrumbs-link:hover {
	color: var(--accent-color);
	background-color: var(--hover-bg);
}

.breadcrumbs-item.active {
	opacity: 1;
	font-weight: 500;
}

.theme-toggle {
	position: fixed;
	top: 1rem;
	right: 1rem;
	background: var(--bg-color);
	color: var(--text-color);
	border: 1px solid rgba(0, 0, 0, 0.1);
	padding: 0.5rem 1rem;
	border-radius: 8px;
	cursor: pointer;
	transition: all 0.3s ease;
	box-shadow: var(--shadow);
}

.theme-toggle:hover {
	background-color: var(--hover-bg);
}

@media (max-width: 480px) {
	.breadcrumbs {
		padding: 0.75rem;
	}

	.breadcrumbs-item {
		font-size: 0.8rem;
	}

	.breadcrumbs-item:not(:last-child)::after {
		font-size: 1rem;
		margin-left: 0.25rem;
	}

	.theme-toggle {
		padding: 0.4rem 0.8rem;
		font-size: 0.9rem;
	}
}

script:
document.addEventListener("DOMContentLoaded", () => {
	// Theme toggle functionality
	const themeToggle = document.querySelector(".theme-toggle");
	const body = document.body;

	themeToggle.addEventListener("click", () => {
		body.classList.toggle("dark-theme");
		const isDark = body.classList.contains("dark-theme");
		themeToggle.textContent = isDark ? "Toggle Light Mode" : "Toggle Dark Mode";
		localStorage.setItem("theme", isDark ? "dark" : "light");
	});

	// Load saved theme
	const savedTheme = localStorage.getItem("theme");
	if (savedTheme === "dark") {
		body.classList.add("dark-theme");
		themeToggle.textContent = "Toggle Light Mode";
	}

	// Breadcrumb click handling
	document.querySelectorAll(".breadcrumbs-link").forEach((link) => {
		link.addEventListener("click", (e) => {
			e.preventDefault();
			console.log(`Navigating to: ${e.target.href}`);
		});
	});
});
