style:
:root {
    --bg: #ffffff;
    --text: #2d3748;
    --primary: #4f46e5;
    --secondary: #6366f1;
    --surface: #f8fafc;
    --border: #e2e8f0;
  }
  [data-theme="dark"] {
    --bg: #0f172a;
    --text: #f8fafc;
    --primary: #818cf8;
    --secondary: #a5b4fc;
    --surface: #1e293b;
    --border: #334155;
  }
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
  }
  body {
    min-height: 100vh;
    display: flex;
    align-items: center;
    justify-content: center;
    background: var(--bg);
    color: var(--text);
    transition: all 0.3s ease;
    font-family: "Inter", sans-serif;
  }
  .theme-toggle {
    position: fixed;
    top: 2rem;
    right: 2rem;
    width: 44px;
    height: 24px;
    background: var(--surface);
    border-radius: 12px;
    border: 1px solid var(--border);
    cursor: pointer;
    transition: all 0.3s ease;
  }
  .theme-toggle::before {
    content: "";
    position: absolute;
    width: 18px;
    height: 18px;
    background: var(--primary);
    border-radius: 50%;
    top: 2px;
    left: 3px;
    transition: transform 0.3s ease;
  }
  [data-theme="dark"] .theme-toggle::before {
    transform: translateX(20px);
  }
  .breadcrumb {
    display: flex;
    align-items: center;
    background: var(--surface);
    border-radius: 12px;
    padding: 12px 24px;
    box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
    overflow-x: auto;
    max-width: 95vw;
    gap: 8px;
    scrollbar-width: thin;
    scrollbar-color: var(--primary) var(--surface);
  }
  .breadcrumb-item {
    display: flex;
    align-items: center;
    gap: 8px;
    flex-shrink: 0;
  }
  .breadcrumb-item a {
    text-decoration: none;
    color: var(--text);
    font-weight: 500;
    padding: 8px 12px;
    border-radius: 8px;
    transition: all 0.2s ease;
  }
  .breadcrumb-item a:hover {
    background: rgba(79, 70, 229, 0.1);
    color: var(--primary);
  }
  .separator {
    color: var(--text);
    opacity: 0.4;
    user-select: none;
  }
  .current-page {
    color: var(--primary);
    font-weight: 600;
    padding: 0 8px;
  }
  .breadcrumb::-webkit-scrollbar {
    height: 4px;
  }
  .breadcrumb::-webkit-scrollbar-track {
    background: var(--surface);
  }
  .breadcrumb::-webkit-scrollbar-thumb {
    background: var(--primary);
    border-radius: 4px;
  }
  @media (max-width: 640px) {
    .breadcrumb {
      padding: 8px 16px;
    }
    .breadcrumb-item a {
      font-size: 0.875rem;
      padding: 6px 10px;
    }
    .theme-toggle {
      top: 1rem;
      right: 1rem;
    }
  }

script:
const themeToggle = document.querySelector(".theme-toggle");
const body = document.body;

themeToggle.addEventListener("click", () => {
  const isDark = body.getAttribute("data-theme") === "dark";
  body.setAttribute("data-theme", isDark ? "" : "dark");
  localStorage.setItem("theme", isDark ? "light" : "dark");
});

const savedTheme = localStorage.getItem("theme") || "light";
body.setAttribute("data-theme", savedTheme === "dark" ? "dark" : "");

document.querySelectorAll(".breadcrumb-item a").forEach((link) => {
  link.addEventListener("click", (e) => {
    e.preventDefault();
    link.animate([{ transform: "scale(1)" }, { transform: "scale(0.95)" }, { transform: "scale(1)" }], { duration: 200, easing: "ease-out" });
  });
});
