style:
:root {
	--bg: #ffffff;
	--text: #2d3748;
	--primary: #6366f1;
	--secondary: #818cf8;
	--border: #e2e8f0;
	--shadow: rgba(0, 0, 0, 0.1);
}

[data-theme="dark"] {
	--bg: #0f172a;
	--text: #f8fafc;
	--primary: #818cf8;
	--secondary: #a5b4fc;
	--border: #1e293b;
	--shadow: rgba(255, 255, 255, 0.05);
}

body {
	margin: 0;
	min-height: 100vh;
	display: flex;
	align-items: center;
	justify-content: center;
	background: var(--bg);
	color: var(--text);
	transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
	font-family: "Inter", system-ui, sans-serif;
}

.theme-toggle {
	position: fixed;
	top: 2rem;
	right: 2rem;
	background: var(--primary);
	border: none;
	width: 3rem;
	height: 3rem;
	border-radius: 50%;
	cursor: pointer;
	display: flex;
	align-items: center;
	justify-content: center;
	box-shadow: 0 4px 6px var(--shadow);
	transition: all 0.3s ease;
}

.theme-toggle:hover {
	transform: rotate(180deg) scale(1.1);
	background: var(--secondary);
}

.theme-toggle svg {
	width: 1.5rem;
	height: 1.5rem;
	fill: var(--bg);
}

.breadcrumb {
	display: flex;
	align-items: center;
	flex-wrap: wrap;
	background: var(--bg);
	border-radius: 1.5rem;
	padding: 1.25rem 2rem;
	box-shadow: 0 10px 15px var(--shadow);
	border: 1px solid var(--border);
	position: relative;
	gap: 1rem;
}

.breadcrumb::before {
	content: "";
	position: absolute;
	top: 0;
	left: 0;
	width: 100%;
	height: 100%;
	background: linear-gradient(45deg, var(--primary), var(--secondary));
	opacity: 0.1;
	z-index: 0;
}

.breadcrumb-item {
	position: relative;
	display: flex;
	align-items: center;
}

.breadcrumb-item a {
	text-decoration: none;
	color: var(--text);
	font-weight: 500;
	padding: 0.5rem 1rem;
	border-radius: 0.75rem;
	transition: all 0.3s ease;
	position: relative;
	z-index: 1;
	background: var(--bg);
}

.breadcrumb-item a:hover {
	color: var(--primary);
	transform: translateY(-2px);
	box-shadow: 0 4px 6px var(--shadow);
}

.breadcrumb-item:not(:last-child)::after {
	content: "âž”";
	color: var(--text);
	opacity: 0.4;
	font-size: 0.9em;
	transition: opacity 0.3s ease;
}

.current-page {
	color: var(--primary);
	font-weight: 600;
	padding: 0.5rem 1rem;
	border-radius: 0.75rem;
	background: var(--bg);
	box-shadow: inset 0 0 0 2px var(--primary);
}

@media (max-width: 640px) {
	.breadcrumb {
		padding: 1rem;
		border-radius: 1rem;
		max-width: 90vw;
	}

	.breadcrumb-item a {
		font-size: 0.875rem;
		padding: 0.5rem;
	}

	.theme-toggle {
		top: 1rem;
		right: 1rem;
		width: 2.5rem;
		height: 2.5rem;
	}
}

script:
const themeToggle = document.querySelector(".theme-toggle");
const body = document.body;

// Theme toggle
themeToggle.addEventListener("click", () => {
	const isDark = body.getAttribute("data-theme") === "dark";
	body.setAttribute("data-theme", isDark ? "" : "dark");
	localStorage.setItem("theme", isDark ? "light" : "dark");

	// Animate icon
	themeToggle.style.transform = `rotate(${isDark ? 0 : 180}deg) scale(1.1)`;
	setTimeout(() => {
		themeToggle.style.transform = "";
	}, 300);
});

// Load saved theme
const savedTheme = localStorage.getItem("theme") || "light";
body.setAttribute("data-theme", savedTheme === "dark" ? "dark" : "");

// Breadcrumb interaction
document.querySelectorAll(".breadcrumb-item a").forEach((link) => {
	link.addEventListener("click", (e) => {
		e.preventDefault();
		const ripple = document.createElement("div");
		ripple.style.cssText = `
			position: absolute;
			width: 40px;
			height: 40px;
			background: rgba(99, 102, 241, 0.2);
			border-radius: 50%;
			transform: translate(-50%, -50%);
			pointer-events: none;
			animation: ripple 0.6s linear;
		`;

		const rect = e.target.getBoundingClientRect();
		ripple.style.left = `${e.clientX - rect.left}px`;
		ripple.style.top = `${e.clientY - rect.top}px`;

		e.target.appendChild(ripple);
		setTimeout(() => ripple.remove(), 600);
	});
});
