style:
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  min-height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  background: linear-gradient(45deg, #6a11cb, #2575fc);
  font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
}

.card {
  width: 400px;
  background: white;
  border-radius: 20px;
  overflow: hidden;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s;
}

.card:hover {
  transform: translateY(-5px);
}

.card-header {
  background: #2575fc;
  color: white;
  padding: 20px;
  text-align: center;
}

.card-header h2 {
  font-size: 1.5rem;
  margin-bottom: 10px;
}

.card-header p {
  font-size: 0.9rem;
  opacity: 0.8;
}

.card-content {
  padding: 20px;
}

.car-details {
  display: flex;
  justify-content: space-between;
  margin-bottom: 20px;
}

.car-details .car-image {
  width: 150px;
  height: 100px;
  border-radius: 10px;
  overflow: hidden;
}

.car-details .car-image img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.car-details .car-info {
  flex: 1;
  margin-left: 20px;
}

.car-details .car-info h3 {
  font-size: 1.2rem;
  margin-bottom: 10px;
  color: #2d3436;
}

.car-details .car-info p {
  font-size: 0.9rem;
  color: #666;
}

.fuel-type {
  display: flex;
  justify-content: space-between;
  margin: 20px 0;
}

.fuel-type button {
  flex: 1;
  padding: 10px;
  border: 1px solid #ddd;
  border-radius: 10px;
  background: #f8f9fa;
  cursor: pointer;
  transition: all 0.3s;
  text-align: center;
}

.fuel-type button.active {
  background: #2575fc;
  color: white;
  border-color: #2575fc;
}

.additional-options {
  margin: 20px 0;
}

.additional-options label {
  display: block;
  margin-bottom: 10px;
  font-size: 0.9rem;
  color: #666;
}

.additional-options input[type="checkbox"] {
  margin-right: 10px;
}

.price-breakdown {
  margin: 20px 0;
}

.price-breakdown div {
  display: flex;
  justify-content: space-between;
  margin-bottom: 10px;
  font-size: 0.9rem;
  color: #666;
}

.price-breakdown .total {
  font-size: 1.2rem;
  color: #2d3436;
  font-weight: bold;
}

.book-btn {
  width: 100%;
  padding: 15px;
  background: linear-gradient(45deg, #2575fc, #1a5bbf);
  color: white;
  border: none;
  border-radius: 10px;
  font-size: 1.1rem;
  cursor: pointer;
  transition: transform 0.2s;
  margin-top: 20px;
}

.book-btn:hover {
  transform: translateY(-2px);
}

script:
function selectFuelType(btn, type) {
  // Remove active class from all buttons
  document.querySelectorAll(".fuel-type button").forEach((b) => b.classList.remove("active"));
  // Add active class to clicked button
  btn.classList.add("active");
  updatePrice();
}

function updatePrice() {
  const basePrice = 49;
  let additionalPrice = 0;

  if (document.getElementById("insurance").checked) additionalPrice += 10;
  if (document.getElementById("gps").checked) additionalPrice += 5;
  if (document.getElementById("childSeat").checked) additionalPrice += 7;

  document.getElementById("additionalPrice").textContent = `$${additionalPrice}`;
  document.getElementById("totalPrice").textContent = `$${basePrice + additionalPrice}/day`;
}
