style:
body { font-family: sans-serif; display: flex; justify-content: center; align-items: center; min-height: 100vh; background-color: #e8f0fe; }

.card { width: 350px; background-color: #fff; border-radius: 12px; box-shadow: 0 8px 16px rgba(0, 0, 0, 0.1); overflow: hidden; transition: transform 0.2s, box-shadow 0.2s; }

.card:hover { transform: translateY(-8px); box-shadow: 0 12px 20px rgba(0, 0, 0, 0.15); }

.card-image { width: 100%; height: 220px; object-fit: cover; }

.card-content { padding: 25px; }

.card-title { font-size: 1.4rem; font-weight: 700; color: #333; margin-bottom: 12px; }

.card-details { display: flex; align-items: center; justify-content: space-between; margin-bottom: 20px; }

.card-location { color: #666; font-size: 0.95rem; display: flex; align-items: center; gap: 8px; }

.card-location i { color: #007bff; }

.card-rating { color: #f2c14e; font-size: 1rem; }

.card-description { color: #555; margin-bottom: 20px; overflow: hidden; text-overflow: ellipsis; display: -webkit-box; -webkit-line-clamp: 3; line-clamp: 3; -webkit-box-orient: vertical; }

.card-price { font-size: 1.3rem; font-weight: 600; color: #009688; margin-bottom: 25px; }

.card-booking { background-color: #007bff; color: #fff; padding: 14px 25px; border: none; border-radius: 6px; cursor: pointer; font-size: 1.1rem; transition: background-color 0.3s; display: inline-flex; align-items: center; justify-content: center; gap: 10px; }

.card-booking:hover { background-color: #0056b3; }

.card-booking i { font-size: 1.2rem; }
