style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
}

body {
	font-family: 'Arial', sans-serif;
	background-color: #f9f9f9;
	display: flex;
	justify-content: center;
	align-items: center;
	min-height: 100vh;
}

.pricing-container {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
	gap: 2rem;
	width: 90%;
	max-width: 1200px;
}

.pricing-card {
	background: #ffffff;
	border: 2px solid #ddd;
	border-radius: 10px;
	padding: 2rem;
	text-align: center;
	box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
	transition: all 0.3s ease;
}

.pricing-card:hover {
	border-color: #6c63ff;
	transform: translateY(-5px);
}

.pricing-title {
	font-size: 1.5rem;
	font-weight: bold;
	color: #333;
}

.price {
	font-size: 2.5rem;
	color: #6c63ff;
	margin: 1rem 0;
}

.features-list {
	list-style: none;
	text-align: left;
	padding: 0;
	margin: 1rem 0;
}

.features-list li {
	padding: 0.5rem 0;
	border-bottom: 1px solid #ddd;
}

.cta-btn {
	background-color: #6c63ff;
	color: white;
	border: none;
	padding: 0.8rem 1.5rem;
	font-size: 1rem;
	border-radius: 5px;
	cursor: pointer;
	transition: background-color 0.3s ease;
}

.cta-btn:hover {
	background-color: #534acb;
}

.featured-card {
	border-color: #ff9800;
	background-color: #fef8e6;
}
