style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Space Grotesk", sans-serif;
}

body {
	background: #f8f9fa;
	display: flex;
	justify-content: center;
	align-items: center;
	min-height: 100vh;
	padding: 2rem;
}

.pricing-container {
	max-width: 1200px;
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
	gap: 1.5rem;
	width: 100%;
}

.pricing-card {
	background: white;
	padding: 2.5rem;
	border-radius: 12px;
	text-align: center;
	transition: transform 0.2s ease;
	position: relative;
}

.pricing-card:hover {
	transform: translateY(-5px);
}

.plan-name {
	color: #2d3436;
	font-size: 1.3rem;
	font-weight: 600;
	margin-bottom: 1.5rem;
	position: relative;
}

.plan-name::after {
	content: "";
	width: 40px;
	height: 2px;
	background: #2d3436;
	position: absolute;
	bottom: -0.75rem;
	left: 50%;
	transform: translateX(-50%);
}

.price {
	font-size: 2.8rem;
	font-weight: 300;
	color: #2d3436;
	margin: 2rem 0;
}

.price span {
	font-size: 1rem;
	color: #636e72;
}

.features {
	list-style: none;
	margin: 2rem 0;
}

.features li {
	color: #636e72;
	padding: 0.8rem 0;
	border-bottom: 1px solid #f1f1f1;
}

.features li:last-child {
	border-bottom: none;
}

.cta {
	display: block;
	width: 100%;
	padding: 1rem;
	background: transparent;
	color: #2d3436;
	border: 2px solid #2d3436;
	border-radius: 6px;
	font-weight: 500;
	cursor: pointer;
	transition: all 0.2s ease;
}

.cta:hover {
	background: #2d3436;
	color: white;
}

.recommended {
	border: 2px solid #74b9ff;
}

.recommended .cta {
	background: #74b9ff;
	border-color: #74b9ff;
	color: white;
}

.recommended .cta:hover {
	background: #54a9ff;
}

.recommended-badge {
	position: absolute;
	top: 1rem;
	right: 1rem;
	background: #74b9ff;
	color: white;
	padding: 0.25rem 0.75rem;
	border-radius: 20px;
	font-size: 0.8rem;
}
