style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
}

body {
	background-color: #141414;
	color: white;
	min-height: 100vh;
	display: flex;
	flex-direction: column;
	align-items: center;
	padding: 4rem 2rem;
}

.pricing-header {
	text-align: center;
	margin-bottom: 3rem;
}

.pricing-header h1 {
	font-size: 3.5rem;
	margin-bottom: 1rem;
}

.pricing-container {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
	gap: 2rem;
	max-width: 1200px;
	width: 100%;
}

.plan-card {
	background: #181818;
	border-radius: 8px;
	padding: 2.5rem;
	text-align: center;
	position: relative;
	transition: transform 0.3s ease;
}

.plan-card:hover {
	transform: translateY(-5px);
}

.most-popular {
	border: 3px solid #e50914;
	transform: scale(1.05);
}

.plan-name {
	font-size: 1.8rem;
	margin-bottom: 1rem;
	color: #e50914;
}

.price {
	font-size: 3rem;
	margin: 1.5rem 0;
	font-weight: bold;
}

.price span {
	font-size: 1.2rem;
	color: #808080;
}

.features-list {
	list-style: none;
	margin: 2rem 0;
	text-align: left;
}

.features-list li {
	padding: 1rem 0;
	border-bottom: 1px solid #333;
	display: flex;
	align-items: center;
}

.features-list li:last-child {
	border-bottom: none;
}

.check-icon {
	color: #e50914;
	margin-right: 1rem;
	font-size: 1.2rem;
}

.cta-button {
	width: 100%;
	padding: 1.2rem;
	background-color: #e50914;
	color: white;
	border: none;
	border-radius: 4px;
	font-size: 1.1rem;
	font-weight: bold;
	cursor: pointer;
	transition: background-color 0.3s ease;
}

.cta-button:hover {
	background-color: #f6121d;
}

.badge {
	position: absolute;
	top: -15px;
	left: 50%;
	transform: translateX(-50%);
	background: #e50914;
	color: white;
	padding: 0.5rem 1.5rem;
	border-radius: 20px;
	font-size: 0.9rem;
	font-weight: bold;
}

.toggle-container {
	margin-bottom: 2rem;
	display: flex;
	align-items: center;
	justify-content: center;
	gap: 1rem;
}

.toggle-switch {
	position: relative;
	display: inline-block;
	width: 60px;
	height: 34px;
}

.toggle-switch input {
	opacity: 0;
	width: 0;
	height: 0;
}

.slider {
	position: absolute;
	cursor: pointer;
	top: 0;
	left: 0;
	right: 0;
	bottom: 0;
	background-color: #ccc;
	transition: 0.4s;
	border-radius: 34px;
}

.slider:before {
	position: absolute;
	content: "";
	height: 26px;
	width: 26px;
	left: 4px;
	bottom: 4px;
	background-color: white;
	transition: 0.4s;
	border-radius: 50%;
}

input:checked + .slider {
	background-color: #e50914;
}

input:checked + .slider:before {
	transform: translateX(26px);
}

@media (max-width: 768px) {
	.pricing-container {
		grid-template-columns: 1fr;
	}

	.plan-card {
		margin-bottom: 2rem;
	}
}

script:
const toggle = document.getElementById("billingToggle");
const prices = document.querySelectorAll(".price");

toggle.addEventListener("change", function () {
	const monthlyPrices = [9.99, 15.99, 19.99];
	const annualPrices = [8.99, 14.99, 17.99];

	prices.forEach((priceElement, index) => {
		if (this.checked) {
			priceElement.innerHTML = `$${annualPrices[index]}<span>/month</span>`;
		} else {
			priceElement.innerHTML = `$${monthlyPrices[index]}<span>/month</span>`;
		}
	});
});
