style:
*::after,
*::before,
* {
	padding: 0;
	margin: 0;
	box-sizing: border-box;
}

.product-card {
	background: white;
	border-radius: 16px;
	padding: 20px;
	width: 280px;
	box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
	 margin:70px;
	cursor: pointer;
	transition: box-shadow 0.4s ease;
	font-family: Arial, Helvetica, sans-serif;
}

.product-card:hover {
	box-shadow: 0 6px 10px rgba(0, 0, 0, 0.3);
}

.product-card .badge {
	padding: 4px 8px;
	background-color: orangered;
	font-weight: bold;
	font-size: 14px;
	color: white;
	border-radius: 6px;
}

.product-card .image-container {
	width: 100%;
	aspect-ratio: 1;
	margin-top: 10px;
	overflow: hidden;
	border-radius: 8px;
}

.image-container img {
	width: 100%;
	height: 100%;
	object-fit: cover;
}

.product-card h1 {
	font-size: 20px;
	color: #222;
	margin: 8px 0 4px;
	font-weight: 600;
}

.product-card p {
	color: #666;
	font-size: 14px;
	font-weight: 600;
}

.product-card .card-bottom {
	display: flex;
	align-items: center;
	justify-content: space-between;
	margin: 10px 0 0;
}

.card-bottom .price {
	font-size: 16px;
	color: gray;
}

.card-bottom .buy-btn {
	display: inline-block;
	background-color: #4a90e2;
	color: white;
	font-size: 16px;
	padding: 8px 16px;
	border: none;
	border-radius: 12px;
	text-decoration: none;
	cursor: pointer;
}

.card-bottom .buy-btn:hover {
	background-color: #357ab8;
}
