style:
body {
	display: flex;
	justify-content: center;
	align-items: center;
	min-height: 100vh;
	margin: 0;
	background-color: #ffeedd;
	font-family: "Comic Neue", cursive;
}

.comic-panel {
	background: #fff;
	width: 280px;
	padding: 25px;
	border: 4px solid #000;
	border-radius: 20px;
	box-shadow: 8px 8px 0 #000;
	position: relative;
	transform: scale(1);
	transition: all 0.3s cubic-bezier(0.175, 0.885, 0.32, 1.275);
}

.comic-panel:hover {
	transform: scale(1.02);
	box-shadow: 12px 12px 0 #000;
}

.product-image {
	width: 100%;
	height: 220px;
	border: 3px solid #000;
	border-radius: 15px;
	margin-bottom: 20px;
	object-fit: cover;
	background: #ffd700;
	padding: 5px;
}

.burst-badge {
	position: absolute;
	top: -20px;
	left: -20px;
	background: #ff4444;
	color: white;
	padding: 12px 18px;
	border: 3px solid #000;
	border-radius: 50%;
	font-weight: 900;
	transform: rotate(-15deg);
	clip-path: polygon(50% 0%, 100% 38%, 82% 100%, 18% 100%, 0% 38%);
}

.product-title {
	color: #2b2b2b;
	margin: 0 0 15px 0;
	font-size: 1.8em;
	text-shadow: 2px 2px 0 #ffd700;
	letter-spacing: -1px;
}

.product-description {
	font-size: 1em;
	margin-bottom: 20px;
	line-height: 1.4;
	background: #f8f8f8;
	padding: 15px;
	border: 2px dashed #000;
	border-radius: 10px;
}

.price-tag {
	background: #00c853;
	display: inline-block;
	padding: 8px 20px;
	border: 3px solid #000;
	border-radius: 30px;
	font-weight: 900;
	font-size: 1.4em;
	margin-bottom: 15px;
	position: relative;
}

.price-tag::after {
	content: "ðŸ’°";
	position: absolute;
	right: -15px;
	top: -15px;
	font-size: 1.2em;
}

.cta-button {
	display: block;
	width: 100%;
	padding: 15px;
	background: #ff6b6b;
	border: 3px solid #000;
	border-radius: 10px;
	font-weight: 900;
	text-transform: uppercase;
	letter-spacing: 1px;
	cursor: pointer;
	transition: all 0.2s ease;
	box-shadow: 4px 4px 0 #000;
}

.cta-button:hover {
	background: #ff4444;
	transform: translateY(-2px);
	box-shadow: 6px 6px 0 #000;
}

.comic-dots {
	position: absolute;
	bottom: -30px;
	right: -20px;
	font-size: 2em;
	transform: rotate(10deg);
	opacity: 0.8;
}

.star-rating {
	margin: 10px 0;
	font-size: 1.4em;
	color: #ffd700;
	text-shadow: 1px 1px 0 #000;
}
