style:
:root {
	--primary: #e74c3c;
	--secondary: #c0392b;
	--text: #2c3e50;
	--background: #f9f9f9;
	--item-gap: 1rem;
}

body {
	font-family: "Roboto", sans-serif;
	background: var(--background);
	min-height: 100vh;
	display: flex;
	justify-content: center;
	align-items: center;
	margin: 0;
}

.profile-card {
	background: white;
	border-radius: 15px;
	box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
	display: flex;
	max-width: 600px;
	width: 90%;
	margin: 1rem;
	position: relative;
	transition: transform 0.3s ease;
	overflow: hidden;
}

.profile-card:hover {
	transform: translateY(-5px);
}

.profile-image {
	flex: 0 0 200px;
	position: relative;
	overflow: hidden;
}

.profile-image img {
	width: 100%;
	height: 100%;
	object-fit: cover;
	transition: transform 0.3s ease;
}

.profile-image:hover img {
	transform: scale(1.05);
}

.profile-content {
	display: flex;
	flex-direction: column;
	padding: 2rem;
	flex: 1;
}

.name {
	color: var(--text);
	font-size: 1.8rem;
}

.role {
	color: var(--primary);
	font-weight: 500;
}

.bio {
	color: var(--text);
	line-height: 1.6;
	opacity: 0.8;
}

.stats {
	display: grid;
	grid-template-columns: repeat(3, 1fr);
	gap: 1rem;
	margin-bottom: 2rem;
}

.stat-item {
	text-align: center;
	padding: 0.5rem;
	background: rgba(231, 76, 60, 0.08);
	border-radius: 8px;
}

.stat-number {
	color: var(--primary);
	font-weight: 700;
	font-size: 1.2rem;
}

.stat-label {
	color: var(--text);
	font-size: 0.8rem;
	opacity: 0.8;
}

.action-buttons {
	display: flex;
	justify-content: space-between;
	gap: 1rem;
}

.follow-button {
	padding: 0.8rem 2rem;
	background: var(--primary);
	color: white;
	border: none;
	border-radius: 25px;
	cursor: pointer;
	transition: all 0.3s ease;
	display: flex;
	align-items: center;
	gap: 0.5rem;
}

.follow-button:hover {
	background: var(--secondary);
	transform: translateY(-2px);
}

.follow-button.following {
	background: #2ecc71;
}

.social-links {
	display: flex;
	align-items: center;
	float: right;
	gap: 1rem;
}

.social-links a {
	color: var(--text);
	transition: color 0.3s ease;
}

.social-links a:hover {
	color: var(--primary);
}

@media (max-width: 768px) {
	.profile-card {
		flex-direction: column;
	}

	.profile-image {
		flex: 0 0 250px;
	}

	.stats {
		grid-template-columns: 1fr;
	}
}

script:
function toggleFollow(button) {
	button.classList.toggle("following");
	const icon = button.querySelector("i");
	const text = button.querySelector("span");

	if (button.classList.contains("following")) {
		icon.className = "fas fa-check";
		text.textContent = "Following";
	} else {
		icon.className = "fas fa-plus";
		text.textContent = "Follow";
	}
}
