style:
body {
	display: flex;
	justify-content: center;
	align-items: center;
	height: 100vh;
	background: #251f1f;
	font-family: "Poppins", sans-serif;
}

.profile-card {
	background: white;
	width: 320px;
	border-radius: 15px;
	box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
	overflow: hidden;
	text-align: center;
}

.cover-photo {
	width: 100%;
	height: 100px;
}

.profile-photo {
	width: 100px;
	height: 100px;
	border-radius: 50%;
	object-fit: cover;
	border: 4px solid white;
	margin-top: -50px;
}

.profile-card h2 {
	margin: 10px 0 5px;
	font-size: 22px;
	color: #333;
}

.profile-card p {
	color: #666;
	font-size: 14px;
	margin-bottom: 10px;
}

.socials {
	margin: 10px 0;
}

.socials a {
	margin: 0 8px;
	color: grey;
	font-size: 20px;
	transition: color 0.3s ease-in-out;
}

.socials a:hover {
	color: #554d4f;
}

.btns {
	padding: 15px;
	display: flex;
	justify-content: center;
	align-items: center;
	gap: 1rem;
}

.btn {
	text-decoration: none;
	padding: 8px 16px;
	border-radius: 20px;
	border: none;
	cursor: pointer;
	font-size: 14px;
	transition: background 0.3s ease-in-out;
	background: grey;
	color: white;
}

.btn:hover {
	background: #554d4f;
}
