style:
:root {
	--bg-color: #f8f9fa;
	--card-bg: #ffffff;
	--text-color: #333;
	--progress-color: #007bff;
}
body.dark-mode {
	--bg-color: #1c1c1c;
	--card-bg: #2a2a2a;
	--text-color: #f0f0f0;
	--progress-color: #ff9800;
}
body {
	margin: 0;
	background-color: var(--bg-color);
	transition: background-color 0.3s;
	font-family: Arial, sans-serif;
	display: flex;
	flex-direction: column;
	align-items: center;
	justify-content: center;
	min-height: 100vh;
	position: relative;
}
.toggle-container {
	position: absolute;
	top: 20px;
	right: 20px;
}
.switch {
	position: relative;
	display: inline-block;
	width: 60px;
	height: 34px;
}
.switch input {
	opacity: 0;
	width: 0;
	height: 0;
}
.slider {
	position: absolute;
	cursor: pointer;
	top: 0;
	left: 0;
	right: 0;
	bottom: 0;
	background-color: #ccc;
	transition: 0.4s;
	border-radius: 34px;
}
.slider:before {
	position: absolute;
	content: "";
	height: 26px;
	width: 26px;
	left: 4px;
	bottom: 4px;
	background-color: white;
	transition: 0.4s;
	border-radius: 50%;
}
input:checked + .slider {
	background-color: var(--progress-color);
}
input:checked + .slider:before {
	transform: translateX(26px);
}
.cards {
	display: flex;
	gap: 30px;
	flex-wrap: wrap;
	justify-content: center;
}
.card {
	background: var(--card-bg);
	color: var(--text-color);
	padding: 20px;
	border-radius: 10px;
	box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
	width: 250px;
	transition: transform 0.3s, box-shadow 0.3s;
}
.card:hover {
	transform: translateY(-5px);
	box-shadow: 0 6px 15px rgba(0, 0, 0, 0.15);
}
.card-header {
	display: flex;
	align-items: center;
	margin-bottom: 15px;
}
.card-header i {
	font-size: 28px;
	margin-right: 10px;
	color: var(--progress-color);
}
.progress-bar-container {
	background: #ddd;
	border-radius: 20px;
	overflow: hidden;
	height: 15px;
	margin-top: 10px;
}
.progress-bar {
	background: var(--progress-color);
	height: 100%;
	width: 0;
	border-radius: 20px;
	transition: width 1s ease-in-out;
}
.progress-circle {
	position: relative;
	width: 100px;
	height: 100px;
	border-radius: 50%;
	background: conic-gradient(var(--progress-color) 0%, var(--progress-color) 60%, #ddd 60% 100%);
	margin: 20px auto;
}
.progress-circle::after {
	content: "";
	position: absolute;
	top: 50%;
	left: 50%;
	transform: translate(-50%, -50%);
	width: 80px;
	height: 80px;
	border-radius: 50%;
	background: var(--card-bg);
}
.progress-circle span {
	position: absolute;
	top: 50%;
	left: 50%;
	transform: translate(-50%, -50%);
	font-weight: bold;
	color: var(--text-color);
}
