style:
body { 
  margin: 0; 
  padding: 0; 
  display: flex; 
  justify-content: center; 
  align-items: center; 
  min-height: 100vh; 
  background: linear-gradient(45deg, #1a1a1a, #2d3436); 
  font-family: 'Segoe UI', sans-serif; 
  gap: 20px; 
  flex-wrap: wrap; 
} 

.card { 
  background: rgba(255, 255, 255, 0.1); 
  backdrop-filter: blur(12px); 
  border-radius: 20px; 
  border: 1px solid rgba(255, 255, 255, 0.2); 
  padding: 30px; 
  width: 300px; 
  color: #fff; 
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1); 
  transition: transform 0.3s ease; 
  cursor: pointer; 
} 

.card:hover { 
  transform: translateY(-10px); 
} 

.card-icon { 
  font-size: 40px; 
  color: #70a1ff; 
  margin-bottom: 20px; 
} 

.card-title { 
  font-size: 24px; 
  font-weight: 600; 
  margin-bottom: 15px; 
} 

.card-text { 
  font-size: 14px; 
  opacity: 0.9; 
  line-height: 1.6; 
  margin-bottom: 25px; 
} 

.progress-bar { 
  height: 4px; 
  background: rgba(255, 255, 255, 0.2); 
  border-radius: 2px; 
  margin-bottom: 20px; 
  overflow: hidden; 
} 

.progress { 
  height: 100%; 
  border-radius: 2px; 
  transition: width 0.3s ease; 
} 

.stats { 
  display: flex; 
  justify-content: space-between; 
  font-size: 12px; 
  opacity: 0.8; 
}
