style:
/* Global Styles */
body {
	margin: 0;
	padding: 0;
	font-family: "Arial", sans-serif;
	background: #f0f0f0;
}
.container {
	display: flex;
	justify-content: center;
	align-items: flex-start;
	gap: 20px;
	padding: 20px;
	flex-wrap: wrap;
}

/* Card Base */
.card {
	border-radius: 10px;
	box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
	width: 300px;
	transition: transform 0.3s, box-shadow 0.3s;
	cursor: pointer;
	overflow: hidden;
}
.card:hover {
	transform: translateY(-5px);
	box-shadow: 0 8px 16px rgba(0, 0, 0, 0.2);
}

/* Light and Dark Modes */
.card.light {
	background-color: #fff;
	color: #333;
}
.card.dark {
	background-color: #333;
	color: #fff;
}

/* Card Header */
.card-header {
	display: flex;
	align-items: center;
	padding: 20px;
	border-bottom: 1px solid rgba(0, 0, 0, 0.1);
}
/* Adjust header border for dark mode */
.card.dark .card-header {
	border-bottom: 1px solid rgba(255, 255, 255, 0.2);
}
.card-header i {
	font-size: 24px;
	margin-right: 10px;
}
.card-header h2 {
	font-size: 20px;
	margin: 0;
}

/* Card Body */
.card-body {
	padding: 20px;
}
.card-body p {
	margin: 0 0 10px;
}

/* Progress Bar Container */
.progress-bar {
	background-color: #e0e0e0;
	border-radius: 5px;
	overflow: hidden;
	height: 20px;
	margin-top: 10px;
}
/* Dark mode progress bar background */
.card.dark .progress-bar {
	background-color: #555;
}

/* Progress Indicator */
.progress {
	height: 100%;
	width: 0;
	background-color: #4caf50;
	transition: width 1s ease-in-out;
}
/* Dark mode progress color */
.card.dark .progress {
	background-color: #81c784;
}

/* On card hover, animate the progress bar using the CSS custom property --progress */
.card:hover .progress {
	width: var(--progress);
}
