style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Arial", sans-serif;
}

body {
	min-height: 100vh;
	display: flex;
	justify-content: center;
	align-items: center;
	gap: 2rem;
	padding: 20px;
	background: #f0f2f5;
	flex-wrap: wrap;
}

.card {
	width: 300px;
	padding: 25px;
	border-radius: 20px;
	transition: all 0.3s ease;
}

/* Light Theme */

.card.light {
	background: white;
	box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1);
}

.card.light .title {
	color: #333;
}

.card.light .subtitle {
	color: #666;
}

/* Dark Theme */

.card.dark {
	background: #1a1a1a;
	box-shadow: 0 10px 20px rgba(0, 0, 0, 0.3);
}

.card.dark .title {
	color: #fff;
}

.card.dark .subtitle {
	color: #aaa;
}

.card:hover {
	transform: translateY(-5px);
}

.title {
	font-size: 1.5rem;
	font-weight: bold;
	margin-bottom: 5px;
}

.subtitle {
	font-size: 0.9rem;
	margin-bottom: 25px;
}

.progress-section {
	margin: 15px 0;
}

.progress-info {
	display: flex;
	justify-content: space-between;
	margin-bottom: 8px;
}

.progress-label {
	font-size: 0.9rem;
}

.light .progress-label {
	color: #555;
}

.dark .progress-label {
	color: #ddd;
}

.progress-percentage {
	font-weight: bold;
}

.light .progress-percentage {
	color: #333;
}

.dark .progress-percentage {
	color: #fff;
}

.progress-bar {
	height: 8px;
	background: #e0e0e0;
	border-radius: 4px;
	overflow: hidden;
	position: relative;
}

.progress-fill {
	height: 100%;
	border-radius: 4px;
	transition: width 0.5s ease-in-out;
	position: relative;
	overflow: hidden;
}

.light .progress-fill {
	background: #4caf50;
}

.dark .progress-fill {
	background: #00ff9d;
}

.progress-fill::after {
	content: "";
	position: absolute;
	top: 0;
	left: 0;
	width: 100%;
	height: 100%;
	background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
	animation: shimmer 1.5s infinite;
}

.stats {
	display: flex;
	justify-content: space-between;
	margin-top: 25px;
	padding-top: 20px;
	border-top: 1px solid;
}

.light .stats {
	border-color: #eee;
}

.dark .stats {
	border-color: #333;
}

.stat-item {
	text-align: center;
}

.stat-value {
	font-size: 1.2rem;
	font-weight: bold;
	margin-bottom: 5px;
}

.light .stat-value {
	color: #333;
}

.dark .stat-value {
	color: #fff;
}

.stat-label {
	font-size: 0.8rem;
	text-transform: uppercase;
}

.light .stat-label {
	color: #666;
}

.dark .stat-label {
	color: #888;
}

.icon {
	font-size: 1.2rem;
	margin-right: 8px;
}

.light .icon {
	color: #4caf50;
}

.dark .icon {
	color: #00ff9d;
}

@keyframes shimmer {
	0% {
		transform: translateX(-100%);
	}
	100% {
		transform: translateX(100%);
	}
}
