style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Segoe UI", sans-serif;
}

body {
	min-height: 100vh;
	display: flex;
	justify-content: center;
	align-items: center;
	gap: 2rem;
	padding: 20px;
	background: #f4f6f8;
	flex-wrap: wrap;
}

.progress-card {
	width: 340px;
	border-radius: 16px;
	transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
	position: relative;
	overflow: hidden;
}

.progress-card.light {
	background: linear-gradient(145deg, #ffffff, #f0f0f0);
	box-shadow: 5px 5px 10px rgba(163, 177, 198, 0.5), -5px -5px 10px rgba(255, 255, 255, 0.6);
}

.progress-card.dark {
	background: linear-gradient(145deg, #2d3436, #222831);
	box-shadow: 5px 5px 10px rgba(0, 0, 0, 0.3), -5px -5px 10px rgba(255, 255, 255, 0.05);
}

.progress-card:hover {
	transform: translateY(-8px) scale(1.02);
}

.card-header {
	padding: 20px;
	position: relative;
}

.light .card-header {
	border-bottom: 1px solid rgba(0, 0, 0, 0.08);
}

.dark .card-header {
	border-bottom: 1px solid rgba(255, 255, 255, 0.08);
}

.card-title {
	font-size: 1.4rem;
	font-weight: 600;
	margin-bottom: 8px;
	display: flex;
	align-items: center;
	gap: 10px;
}

.light .card-title {
	color: #2d3436;
}

.dark .card-title {
	color: #ecf0f1;
}

.card-subtitle {
	font-size: 0.9rem;
	opacity: 0.7;
}

.light .card-subtitle {
	color: #636e72;
}

.dark .card-subtitle {
	color: #b2bec3;
}

.progress-container {
	padding: 20px;
}

.progress-item {
	margin-bottom: 24px;
}

.progress-header {
	display: flex;
	justify-content: space-between;
	margin-bottom: 10px;
}

.progress-label {
	font-size: 0.9rem;
	font-weight: 500;
}

.light .progress-label {
	color: #2d3436;
}

.dark .progress-label {
	color: #dfe6e9;
}

.progress-value {
	font-size: 0.9rem;
	font-weight: 600;
}

.light .progress-value {
	color: #0984e3;
}

.dark .progress-value {
	color: #74b9ff;
}

.progress-bar {
	height: 8px;
	background: rgba(0, 0, 0, 0.1);
	border-radius: 4px;
	overflow: hidden;
	position: relative;
}

.dark .progress-bar {
	background: rgba(255, 255, 255, 0.1);
}

.progress-fill {
	height: 100%;
	width: 0;
	border-radius: 4px;
	position: relative;
	transition: width 1s ease;
	animation: fillProgress 1.5s ease forwards;
}

.light .progress-fill {
	background: linear-gradient(90deg, #0984e3, #00cec9);
}

.dark .progress-fill {
	background: linear-gradient(90deg, #74b9ff, #81ecec);
}

.metrics {
	display: grid;
	grid-template-columns: repeat(3, 1fr);
	gap: 15px;
	padding: 20px;
	text-align: center;
}

.metric-item {
	padding: 10px;
	border-radius: 12px;
	transition: transform 0.3s ease;
}

.light .metric-item {
	background: rgba(0, 0, 0, 0.03);
}

.dark .metric-item {
	background: rgba(255, 255, 255, 0.05);
}

.metric-item:hover {
	transform: translateY(-3px);
}

.metric-value {
	font-size: 1.4rem;
	font-weight: 700;
	margin-bottom: 5px;
}

.light .metric-value {
	color: #2d3436;
}

.dark .metric-value {
	color: #dfe6e9;
}

.metric-label {
	font-size: 0.8rem;
	text-transform: uppercase;
	letter-spacing: 0.5px;
}

.light .metric-label {
	color: #636e72;
}

.dark .metric-label {
	color: #b2bec3;
}

.status-badge {
	position: absolute;
	top: 20px;
	right: 20px;
	padding: 6px 12px;
	border-radius: 20px;
	font-size: 0.8rem;
	font-weight: 500;
}

.light .status-badge {
	background: rgba(9, 132, 227, 0.1);
	color: #0984e3;
}

.dark .status-badge {
	background: rgba(116, 185, 255, 0.1);
	color: #74b9ff;
}

@keyframes fillProgress {
	from {
		width: 0;
	}
	to {
		width: var(--progress);
	}
}
