style:
* {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

body {
  font-family: "Verdana", sans-serif;
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  background-color: #e6fffa;
}

.card {
  background: white;
  border-radius: 16px;
  box-shadow: 0 10px 20px rgba(0, 0, 0, 0.2);
  max-width: 360px;
  overflow: hidden;
  transition: transform 0.3s, box-shadow 0.3s;
}

.card:hover {
  transform: translateY(-8px);
  box-shadow: 0 16px 30px rgba(0, 0, 0, 0.25);
}

.card img {
  width: 100%;
  height: 220px;
  object-fit: cover;
}

.card-content {
  padding: 20px;
}

.card-title {
  font-size: 1.75rem;
  color: #22543d;
  margin-bottom: 10px;
}

.card-text {
  font-size: 1rem;
  color: #2d3748;
  line-height: 1.6;
}

.button {
  display: inline-block;
  margin-top: 14px;
  padding: 12px 24px;
  background-color: #319795;
  color: white;
  border: none;
  border-radius: 8px;
  text-decoration: none;
  font-size: 0.95rem;
  transition: background-color 0.3s;
}

.button:hover {
  background-color: #2c7a7b;
}
