style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Arial", sans-serif;
}

body {
	display: flex;
	justify-content: center;
	align-items: center;
	min-height: 100vh;
	background: #f0f2f5;
}

.adventure-card {
	width: 350px;
	background: white;
	border-radius: 16px;
	overflow: hidden;
	box-shadow: 0 8px 24px rgba(0, 0, 0, 0.1);
	transition: transform 0.3s ease, box-shadow 0.3s ease;
	margin: 20px;
	position: relative;
}

.adventure-card:hover {
	transform: translateY(-8px);
	box-shadow: 0 12px 28px rgba(0, 0, 0, 0.15);
}

.card-hero {
	position: relative;
	height: 220px;
	overflow: hidden;
}

.card-hero img {
	width: 100%;
	height: 100%;
	object-fit: cover;
	transition: transform 0.4s ease;
}

.adventure-card:hover .card-hero img {
	transform: scale(1.05);
}

.hero-overlay {
	position: absolute;
	top: 0;
	left: 0;
	width: 100%;
	height: 100%;
	background: linear-gradient(180deg, rgba(0, 0, 0, 0) 50%, rgba(0, 0, 0, 0.5) 100%);
}

.card-tag {
	position: absolute;
	top: 15px;
	left: 15px;
	background: rgba(255, 255, 255, 0.9);
	color: #2ecc71;
	padding: 6px 12px;
	border-radius: 20px;
	font-size: 0.9rem;
	font-weight: 600;
	backdrop-filter: blur(4px);
	box-shadow: 0 2px 6px rgba(0, 0, 0, 0.1);
}

.card-content {
	padding: 20px;
}

.card-title {
	color: #2c3e50;
	font-size: 1.5rem;
	margin-bottom: 12px;
	font-weight: 600;
}

.card-description {
	color: #7f8c8d;
	font-size: 0.95rem;
	line-height: 1.6;
	margin-bottom: 20px;
}

.card-stats {
	display: flex;
	justify-content: space-between;
	margin-bottom: 20px;
}

.stat-item {
	text-align: center;
}

.stat-value {
	color: #2ecc71;
	font-size: 1.2rem;
	font-weight: 700;
}

.stat-label {
	color: #95a5a6;
	font-size: 0.85rem;
	text-transform: uppercase;
}

.cta-button {
	display: flex;
	align-items: center;
	justify-content: center;
	gap: 8px;
	background: #2ecc71;
	color: white;
	padding: 12px 24px;
	border: none;
	border-radius: 25px;
	font-weight: 600;
	cursor: pointer;
	transition: all 0.3s ease;
	width: 100%;
	text-transform: uppercase;
	letter-spacing: 0.5px;
}

.cta-button:hover {
	background: #27ae60;
	transform: translateY(-2px);
	box-shadow: 0 4px 12px rgba(46, 204, 113, 0.3);
}

@media (max-width: 480px) {
	.adventure-card {
		width: 90%;
	}
}
