style:
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  font-family: "Arial", sans-serif;
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  background-color: #f9f9f9;
}

.card {
  width: 400px;
  max-width: 90%;
  border-radius: 16px;
  box-shadow: 0 8px 20px rgba(0, 0, 0, 0.1);
  overflow: hidden;
  background: white;
  transition: transform 0.3s ease;
}

.card:hover {
  transform: translateY(-5px);
}

.card img {
  width: 100%;
  height: 200px;
  object-fit: cover;
  position: relative;
}

.card-content {
  padding: 20px;
}

.title {
  font-size: 1.8rem;
  font-weight: bold;
  color: #2a5934;
}

.description {
  font-size: 1rem;
  color: #555;
  margin: 10px 0;
}

.features {
  display: flex;
  gap: 15px;
  margin-top: 15px;
}

.feature-item {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background: #2a5934;
  color: white;
  font-size: 1.4rem;
}

.button {
  display: inline-block;
  text-transform: uppercase;
  background: #2a5934;
  color: white;
  padding: 10px 20px;
  border-radius: 24px;
  margin-top: 20px;
  text-align: center;
  cursor: pointer;
  transition: background-color 0.3s;
}

.button:hover {
  background: #1d4424;
}

@media (max-width: 768px) {
  .card {
    width: 90%;
  }
}
