style:
* {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

body {
  font-family: Arial, sans-serif;
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  background-color: #f3f4f6;
}

.card {
  background: white;
  border-radius: 12px;
  box-shadow: 0 8px 16px rgba(0, 0, 0, 0.15);
  max-width: 350px;
  overflow: hidden;
  transition: transform 0.3s;
}

.card:hover {
  transform: translateY(-5px);
}

.card img {
  width: 100%;
  height: 200px;
  object-fit: cover;
}

.card-content {
  padding: 16px;
}

.card-title {
  font-size: 1.5rem;
  color: #2d3748;
  margin-bottom: 8px;
}

.card-text {
  font-size: 1rem;
  color: #4a5568;
  line-height: 1.5;
}

.button {
  display: inline-block;
  margin-top: 12px;
  padding: 10px 20px;
  background-color: #38a169;
  color: white;
  border: none;
  border-radius: 6px;
  text-decoration: none;
  font-size: 0.9rem;
  transition: background-color 0.3s;
}

.button:hover {
  background-color: #2f855a;
}
