style:
.testimonial-card.classic {
	background: #fff;
	border-radius: 15px;
	padding: 2rem;
	box-shadow: 0 10px 20px rgba(0, 0, 0, 0.05);
	max-width: 400px;
	position: relative;
}

.quote-icon {
	font-size: 4rem;
	color: #4a90e2;
	line-height: 1;
	margin-bottom: 1rem;
}

.user-avatar {
	width: 60px;
	height: 60px;
	border-radius: 50%;
	object-fit: cover;
}

.user-info {
	display: flex;
	align-items: center;
	gap: 1rem;
	margin-top: 1.5rem;
}
