style:
body {
	background: #f5f5f5;
	font-family: Arial, sans-serif;
	display: flex;
	justify-content: center;
	align-items: center;
	min-height: 100vh;
	margin: 0;
}

.testimonial-card {
	background: #fff;
	padding: 2rem;
	max-width: 400px;
	width: 90%;
	border-radius: 10px;
	box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
	position: relative;
	text-align: center;
}

.testimonial-card .quote-icon {
	font-size: 2.5rem;
	color: #f39c12;
	position: absolute;
	top: -20px;
	left: 50%;
	transform: translateX(-50%);
	background: #fff;
	padding: 10px;
	border-radius: 50%;
	border: 3px solid #f39c12;
}

.testimonial-card p {
	font-size: 1rem;
	color: #333;
	line-height: 1.6;
	margin-top: 2rem;
}

.testimonial-card .user {
	margin-top: 1.5rem;
	display: flex;
	flex-direction: column;
	align-items: center;
}

.testimonial-card .user img {
	width: 80px;
	height: 80px;
	border-radius: 50%;
	object-fit: cover;
	border: 3px solid #f39c12;
}

.testimonial-card .user h3 {
	margin: 0.5rem 0 0;
	font-size: 1.2rem;
	color: #333;
}

.testimonial-card .user span {
	font-size: 0.9rem;
	color: #777;
}
