style:
/* Basic reset and body styling */
body {
	background: #f9f9f9;
	font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
	display: flex;
	justify-content: center;
	align-items: center;
	min-height: 100vh;
	margin: 0;
}

/* Testimonial Card Styling */
.testimonial-card {
	background: linear-gradient(to bottom, #ffffff, #f0f0f0);
	padding: 2rem;
	max-width: 360px;
	width: 90%;
	border-radius: 20px;
	box-shadow: 0 6px 12px rgba(0, 0, 0, 0.15);
	position: relative;
	text-align: center;
}

/* Quote icon styling */
.testimonial-card .quote-icon {
	font-size: 3rem;
	color: #3498db;
	position: absolute;
	top: -25px;
	left: 50%;
	transform: translateX(-50%);
	background: #fff;
	padding: 10px;
	border-radius: 50%;
	border: 3px solid #3498db;
}

/* Testimonial text */
.testimonial-card p {
	font-size: 1rem;
	color: #333;
	line-height: 1.6;
	margin: 2.5rem 0 1.5rem;
	font-style: italic;
}

/* User info container */
.testimonial-card .user {
	display: flex;
	align-items: center;
	justify-content: center;
	gap: 1rem;
}

/* User image */
.testimonial-card .user img {
	width: 70px;
	height: 70px;
	border-radius: 50%;
	object-fit: cover;
	border: 2px solid #3498db;
}

/* User name */
.testimonial-card .user-info h3 {
	margin: 0;
	font-size: 1.2rem;
	color: #333;
}

/* User role */
.testimonial-card .user-info span {
	font-size: 0.9rem;
	color: #777;
}
