style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Inter", sans-serif;
}

.cta-section {
	background: linear-gradient(rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0.7)),
		url("https://picsum.photos/1920/1080") center/cover;
	padding: 100px 20px;
	color: white;
	position: relative;
}

.cta-container {
	max-width: 1200px;
	margin: 0 auto;
	text-align: center;
}

.cta-tagline {
	color: #ffd700;
	text-transform: uppercase;
	letter-spacing: 2px;
	margin-bottom: 15px;
	font-size: 1.1rem;
}

.cta-title {
	font-size: 2.8rem;
	margin-bottom: 25px;
	line-height: 1.2;
}

.search-form {
	background: rgba(255, 255, 255, 0.9);
	padding: 40px;
	border-radius: 15px;
	max-width: 800px;
	margin: 0 auto 40px;
	box-shadow: 0 10px 30px rgba(0, 0, 0, 0.2);
}

.form-grid {
	display: grid;
	grid-template-columns: repeat(3, 1fr);
	gap: 20px;
	margin-bottom: 30px;
}

.form-group {
	position: relative;
}

.form-group label {
	display: block;
	text-align: left;
	color: #333;
	margin-bottom: 8px;
	font-weight: 500;
}

.form-control {
	width: 100%;
	padding: 12px 20px;
	border: 2px solid #ddd;
	border-radius: 8px;
	font-size: 1rem;
	transition: all 0.3s ease;
}

.form-control:focus {
	outline: none;
	border-color: #007bff;
	box-shadow: 0 0 0 3px rgba(0, 123, 255, 0.25);
}

.cta-button {
	background: #007bff;
	color: white;
	padding: 16px 45px;
	border: none;
	border-radius: 8px;
	font-size: 1.1rem;
	cursor: pointer;
	transition: all 0.3s ease;
	display: inline-flex;
	align-items: center;
	gap: 10px;
}

.cta-button:hover {
	background: #0056b3;
	transform: translateY(-2px);
}

.benefits {
	display: flex;
	justify-content: center;
	gap: 40px;
	margin-top: 50px;
	flex-wrap: wrap;
}

.benefit-item {
	display: flex;
	align-items: center;
	gap: 15px;
}

.benefit-icon {
	width: 40px;
	height: 40px;
	background: rgba(255, 255, 255, 0.1);
	border-radius: 50%;
	display: flex;
	align-items: center;
	justify-content: center;
}

.benefit-text {
	text-align: left;
}

.benefit-title {
	font-weight: 600;
	margin-bottom: 5px;
}

.benefit-description {
	color: #cccccc;
	font-size: 0.9rem;
}

@media (max-width: 768px) {
	.form-grid {
		grid-template-columns: 1fr;
	}

	.cta-title {
		font-size: 2rem;
	}

	.search-form {
		padding: 25px;
	}

	.benefits {
		gap: 25px;
	}
}

@media (max-width: 480px) {
	.cta-section {
		padding: 60px 20px;
	}

	.cta-title {
		font-size: 1.8rem;
	}

	.cta-button {
		width: 100%;
		justify-content: center;
	}
}

script:
// Random background image loader
document.addEventListener("DOMContentLoaded", function () {
	const ctaSection = document.querySelector(".cta-section");
	const randomImageId = Math.floor(Math.random() * 1000);
	ctaSection.style.backgroundImage = `linear-gradient(rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0.7)),
		 url('https://picsum.photos/id/${randomImageId}/1920/1080')`;
});

// Date input initialization
const today = new Date().toISOString().split("T")[0];
document.getElementById("pickup-date").min = today;
document.getElementById("return-date").min = today;

// Form validation
document.querySelector(".cta-button").addEventListener("click", function (e) {
	const pickupLocation = document.getElementById("pickup-location").value;
	const pickupDate = document.getElementById("pickup-date").value;
	const returnDate = document.getElementById("return-date").value;

	if (!pickupLocation || !pickupDate || !returnDate) {
		alert("Please fill in all required fields");
		e.preventDefault();
	}
});

// Animate benefits on scroll
const benefits = document.querySelectorAll(".benefit-item");
const observer = new IntersectionObserver((entries) => {
	entries.forEach((entry) => {
		if (entry.isIntersecting) {
			entry.target.style.opacity = 1;
			entry.target.style.transform = "translateY(0)";
		}
	});
});

benefits.forEach((benefit) => {
	benefit.style.opacity = 0;
	benefit.style.transform = "translateY(20px)";
	benefit.style.transition = "all 0.6s ease";
	observer.observe(benefit);
});
