style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Inter", sans-serif;
}

.cta-section {
	padding: 120px 20px;
	background: linear-gradient(rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0.7)),
		url("https://picsum.photos/1920/1080?car") center/cover;
	color: white;
	text-align: center;
	position: relative;
}

.cta-container {
	max-width: 1200px;
	margin: 0 auto;
	position: relative;
	z-index: 2;
}

.cta-tag {
	background: #e74c3c;
	color: white;
	padding: 8px 20px;
	border-radius: 30px;
	display: inline-block;
	margin-bottom: 20px;
	font-weight: 600;
}

.cta-title {
	font-size: 3.2rem;
	margin-bottom: 25px;
	line-height: 1.1;
}

.cta-text {
	font-size: 1.2rem;
	margin-bottom: 40px;
	opacity: 0.9;
	max-width: 800px;
	margin-left: auto;
	margin-right: auto;
	line-height: 1.6;
}

.cta-button {
	display: inline-flex;
	align-items: center;
	gap: 12px;
	padding: 18px 45px;
	background: #e74c3c;
	color: white;
	border: none;
	border-radius: 8px;
	font-size: 1.1rem;
	font-weight: 600;
	cursor: pointer;
	transition: all 0.3s ease;
	text-decoration: none;
}

.cta-button:hover {
	background: #c0392b;
	transform: translateY(-3px);
	box-shadow: 0 10px 20px rgba(0, 0, 0, 0.2);
}

.benefits {
	display: flex;
	justify-content: center;
	gap: 40px;
	margin-top: 60px;
	flex-wrap: wrap;
}

.benefit-item {
	display: flex;
	align-items: center;
	gap: 12px;
	background: rgba(255, 255, 255, 0.1);
	padding: 15px 25px;
	border-radius: 10px;
	backdrop-filter: blur(5px);
}

.benefit-icon {
	width: 24px;
	height: 24px;
	color: #e74c3c;
}

@media (max-width: 768px) {
	.cta-title {
		font-size: 2.2rem;
	}

	.cta-text {
		font-size: 1rem;
	}

	.benefits {
		gap: 20px;
	}

	.benefit-item {
		padding: 12px 20px;
	}
}

script:
// Add scroll animation for elements
const observer = new IntersectionObserver((entries) => {
	entries.forEach((entry) => {
		if (entry.isIntersecting) {
			entry.target.style.opacity = 1;
			entry.target.style.transform = "translateY(0)";
		}
	});
});

// Animate main elements
const animatedElements = document.querySelectorAll(
	".cta-tag, .cta-title, .cta-text, .cta-button, .benefits",
);
animatedElements.forEach((el) => {
	el.style.opacity = 0;
	el.style.transform = "translateY(30px)";
	el.style.transition = "all 0.6s ease-out";
	observer.observe(el);
});

// Button hover effect
const ctaButton = document.querySelector(".cta-button");
ctaButton.addEventListener("mouseover", () => {
	ctaButton.style.transform = "translateY(-3px) scale(1.05)";
});
ctaButton.addEventListener("mouseout", () => {
	ctaButton.style.transform = "translateY(0) scale(1)";
});
