style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Inter", sans-serif;
}

.cta-section {
	padding: 80px 20px;
}

.cta-container {
	max-width: 1200px;
	margin: 0 auto;
	display: grid;
	grid-template-columns: 1fr 1.2fr;
	gap: 40px;
	align-items: center;
}

.image-column {
	position: relative;
	border-radius: 20px;
	overflow: hidden;
	min-height: 500px;
	height: 500px;
	box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.1);
	order: 2;
}

.cta-image {
	width: 100%;
	height: 100%;
	object-fit: cover;
}

.content-column {
	background: #ffffff;
	padding: 40px;
	border-radius: 20px;
	margin-right: -100px;
	position: relative;
	z-index: 1;
	box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
	order: 1;
}

.cta-tagline {
	color: #007bff;
	text-transform: uppercase;
	letter-spacing: 2px;
	font-size: 0.9rem;
	margin-bottom: 15px;
	display: block;
}

.cta-title {
	font-size: 2.5rem;
	color: #1a1a1a;
	margin-bottom: 25px;
	line-height: 1.2;
}

.cta-text {
	color: #666;
	line-height: 1.6;
	margin-bottom: 30px;
}

.features-grid {
	display: grid;
	grid-template-columns: repeat(2, 1fr);
	gap: 20px;
	margin-bottom: 40px;
}

.feature-item {
	display: flex;
	align-items: center;
	gap: 10px;
}

.feature-icon {
	width: 30px;
	height: 30px;
	background: #f0f6ff;
	border-radius: 8px;
	display: flex;
	align-items: center;
	justify-content: center;
	padding: 5px;
}

.feature-text {
	color: #444;
	font-size: 0.95rem;
}

.cta-button {
	display: inline-flex;
	align-items: center;
	padding: 16px 40px;
	background: #007bff;
	color: white;
	border: none;
	border-radius: 12px;
	font-weight: 600;
	cursor: pointer;
	transition: all 0.3s ease;
	text-decoration: none;
	gap: 10px;
}

.cta-button:hover {
	background: #0056b3;
	transform: translateY(-2px);
	box-shadow: 0 5px 15px rgba(0, 123, 255, 0.3);
}

@media (max-width: 1024px) {
	.cta-container {
		grid-template-columns: 1fr;
	}

	.content-column {
		margin-right: 0;
		margin-top: -80px;
	}

	.image-column {
		min-height: 400px;
		order: 1;
	}
}

@media (max-width: 768px) {
	.cta-title {
		font-size: 2rem;
	}

	.features-grid {
		grid-template-columns: 1fr;
	}
}

script:
document.addEventListener("DOMContentLoaded", function () {
	const carImages = ["vehicle", "car", "mercedes", "bmw", "audi"];
	const randomCar = carImages[Math.floor(Math.random() * carImages.length)];
	const image = document.getElementById("ctaImage");
	image.src = `https://picsum.photos/800/600?${randomCar}`;
});
