style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Inter", system-ui, -apple-system, sans-serif;
}

.cta-section {
	padding: 120px 20px;
	background: linear-gradient(135deg, #6366f1 0%, #a855f7 50%, #ec4899 100%);
	position: relative;
	overflow: hidden;
}

.cta-container {
	max-width: 1000px;
	margin: 0 auto;
	text-align: center;
	position: relative;
	z-index: 2;
}

.cta-card {
	background: rgba(255, 255, 255, 0.95);
	border-radius: 24px;
	padding: 60px 40px;
	box-shadow: 0 32px 64px -12px rgba(0, 0, 0, 0.2);
	backdrop-filter: blur(10px);
	margin: 0 auto;
	max-width: 800px;
}

.cta-title {
	font-size: 2.8rem;
	color: #1f2937;
	margin-bottom: 25px;
	line-height: 1.1;
}

.cta-text {
	font-size: 1.1rem;
	color: #4b5563;
	line-height: 1.7;
	margin-bottom: 35px;
	max-width: 600px;
	margin-left: auto;
	margin-right: auto;
}

.cta-button-group {
	display: flex;
	gap: 20px;
	justify-content: center;
	flex-wrap: wrap;
}

.cta-button {
	display: inline-flex;
	align-items: center;
	gap: 12px;
	padding: 16px 40px;
	border-radius: 12px;
	font-weight: 600;
	transition: all 0.3s ease;
	text-decoration: none;
}

.primary-btn {
	background: #6366f1;
	color: white;
	border: 2px solid #6366f1;
}

.secondary-btn {
	background: transparent;
	color: #1f2937;
	border: 2px solid #e5e7eb;
}

.cta-button:hover {
	transform: translateY(-2px);
	box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1);
}

.primary-btn:hover {
	background: #4f46e5;
}

.secondary-btn:hover {
	background: rgba(255, 255, 255, 0.9);
}

.cta-bg-pattern {
	position: absolute;
	width: 150%;
	height: 150%;
	opacity: 0.1;
	background-image: radial-gradient(#ffffff 10%, transparent 11%);
	background-size: 40px 40px;
	transform: rotate(-15deg);
	top: -25%;
	left: -25%;
	pointer-events: none;
}

@media (max-width: 768px) {
	.cta-card {
		padding: 40px 20px;
	}

	.cta-title {
		font-size: 2rem;
	}

	.cta-text {
		font-size: 1rem;
	}

	.cta-button-group {
		flex-direction: column;
	}

	.cta-button {
		width: 100%;
		justify-content: center;
	}
}

@media (max-width: 480px) {
	.cta-title {
		font-size: 1.8rem;
	}
}

script:
// Animate elements on scroll
const observer = new IntersectionObserver((entries) => {
	entries.forEach((entry) => {
		if (entry.isIntersecting) {
			entry.target.style.opacity = 1;
			entry.target.style.transform = "translateY(0) scale(1)";
		}
	});
});

// Animate card entrance
const ctaCard = document.querySelector(".cta-card");
ctaCard.style.opacity = "0";
ctaCard.style.transform = "translateY(30px) scale(0.95)";
ctaCard.style.transition = "all 0.6s cubic-bezier(0.4, 0, 0.2, 1)";
observer.observe(ctaCard);

// Add hover effect to card
ctaCard.addEventListener("mousemove", (e) => {
	const rect = ctaCard.getBoundingClientRect();
	const x = (e.clientX - rect.left) / rect.width - 0.5;
	const y = (e.clientY - rect.top) / rect.height - 0.5;

	ctaCard.style.transform = `
		translateY(-5px)
		rotateX(${y * 4}deg)
		rotateY(${-x * 4}deg)
		scale(1.02)
	`;
	ctaCard.style.boxShadow = `
		${x * 15}px ${y * 15}px 40px rgba(0,0,0,0.1)
	`;
});

ctaCard.addEventListener("mouseleave", () => {
	ctaCard.style.transform = "translateY(0) rotateX(0) rotateY(0) scale(1)";
	ctaCard.style.boxShadow = "0 32px 64px -12px rgba(0,0,0,0.2)";
});
