style:
body {
	font-family: "Roboto", sans-serif;
	background: #f6f6f6;
	color: #333;
	margin: 0;
	padding: 0;
	transition: background-color 0.3s, color 0.3s;
}

h1 {
	text-align: center;
	color: #e74c3c;
	padding: 20px 0;
}

.container {
	max-width: 1200px;
	margin: 0 auto;
	padding: 0 20px;
}

.features {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
	gap: 20px;
}

.feature {
	background: #ffffff;
	border-radius: 10px;
	overflow: hidden;
	box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
	transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.feature:hover {
	transform: translateY(-5px);
	box-shadow: 0 6px 12px rgba(0, 0, 0, 0.2);
}

.feature-header {
	background: #e74c3c;
	color: white;
	padding: 15px 20px;
	display: flex;
	align-items: center;
}

.feature-header i {
	font-size: 1.5em;
	margin-right: 10px;
}

.feature-content {
	padding: 20px;
}

.theme-toggle {
	position: fixed;
	top: 20px;
	right: 20px;
	background: #e74c3c;
	border: none;
	padding: 10px;
	border-radius: 50%;
	color: white;
	cursor: pointer;
	transition: transform 0.3s ease;
}

.theme-toggle:hover {
	transform: scale(1.1);
}

.dark-mode {
	background: #2c3e50;
	color: #ecf0f1;
}

.dark-mode .feature {
	background: #34495e;
	box-shadow: 0 4px 8px rgba(0, 0, 0, 0.3);
}

.dark-mode .feature-header {
	background: #c0392b;
}

script:
function toggleTheme() {
	document.body.classList.toggle("dark-mode");
	const icon = document.getElementById("theme-icon");
	if (icon.classList.contains("fa-sun")) {
		icon.classList.remove("fa-sun");
		icon.classList.add("fa-moon");
	} else {
		icon.classList.remove("fa-moon");
		icon.classList.add("fa-sun");
	}
}
