style:
body {
	font-family: "Lato", sans-serif;
	background: #fafafa;
	color: #4a4a4a;
	margin: 0;
	padding: 0;
	transition: background-color 0.3s, color 0.3s;
}
h1 {
	text-align: center;
	color: #3498db;
	padding: 30px 0;
	font-weight: 300;
}
.container {
	max-width: 1200px;
	margin: 0 auto;
	padding: 0 20px;
}
.features {
	display: flex;
	flex-wrap: wrap;
	justify-content: center;
	gap: 30px;
}
.feature {
	background: #ffffff;
	border-radius: 0;
	border-top: 5px solid #3498db;
	padding: 25px;
	box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
	text-align: center;
	transition: all 0.3s ease;
	width: calc(33.333% - 20px);
	min-width: 200px;
}
.feature:hover {
	transform: translateY(-10px);
	box-shadow: 0 8px 16px rgba(0, 0, 0, 0.1);
}
.feature i {
	font-size: 3em;
	color: #3498db;
	margin-bottom: 20px;
}
.feature h3 {
	margin: 10px 0 5px;
	color: #2c3e50;
	font-weight: 400;
}
.theme-toggle {
	position: fixed;
	top: 20px;
	right: 20px;
	background: none;
	border: none;
	color: #3498db;
	font-size: 30px;
	cursor: pointer;
	transition: transform 0.3s ease;
}
.theme-toggle:hover {
	transform: scale(1.2);
}
.dark-mode {
	background: #34495e;
	color: #ecf0f1;
}
.dark-mode .feature {
	background: #2c3e50;
	border-top-color: #2980b9;
	box-shadow: 0 2px 5px rgba(0, 0, 0, 0.3);
}
.dark-mode .feature i {
	color: #2980b9;
}
.dark-mode .feature h3 {
	color: #ecf0f1;
}

script:
function toggleTheme() {
	document.body.classList.toggle("dark-mode");
	const icon = document.getElementById("theme-icon");
	if (icon.classList.contains("fa-sun")) {
		icon.classList.remove("fa-sun");
		icon.classList.add("fa-moon");
	} else {
		icon.classList.remove("fa-moon");
		icon.classList.add("fa-sun");
	}
}
