style:
body {
	font-family: "Playfair Display", serif;
	background: #f8f8f8;
	color: #333;
	margin: 0;
	padding: 0;
	transition: background-color 0.3s, color 0.3s;
}

h1 {
	text-align: center;
	color: #8e44ad;
	padding: 40px 0;
	font-weight: 400;
	font-size: 2.5em;
}

.container {
	max-width: 1200px;
	margin: 0 auto;
	padding: 0 20px;
}

.features {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
	gap: 30px;
}

.feature {
	background: #fff;
	border-radius: 10px;
	box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1);
	padding: 30px;
	text-align: center;
	position: relative;
	overflow: hidden;
	transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.feature:hover {
	transform: translateY(-5px);
	box-shadow: 0 15px 30px rgba(0, 0, 0, 0.15);
}

.feature::before {
	content: "";
	position: absolute;
	top: 0;
	left: -75%;
	width: 50%;
	height: 100%;
	background: rgba(142, 68, 173, 0.1);
	transform: skewX(-45deg);
	transition: left 0.5s;
}

.feature:hover::before {
	left: 125%;
}

.feature i {
	font-size: 4em;
	color: #8e44ad;
	margin-bottom: 20px;
	transition: color 0.3s;
}

.feature:hover i {
	color: #6c3483;
}

.feature h3 {
	color: #2c3e50;
	margin: 10px 0;
	font-weight: 500;
}

.theme-toggle {
	position: fixed;
	top: 20px;
	right: 20px;
	background: #8e44ad;
	border: none;
	color: white;
	padding: 15px;
	border-radius: 50%;
	font-size: 20px;
	cursor: pointer;
	transition: transform 0.3s ease;
}

.theme-toggle:hover {
	transform: scale(1.1);
}

.dark-mode {
	background: #34495e;
	color: #ecf0f1;
}

.dark-mode .feature {
	background: #2c3e50;
	box-shadow: 0 10px 20px rgba(0, 0, 0, 0.3);
}

.dark-mode .feature i {
	color: #d35400;
}

.dark-mode .feature::before {
	background: rgba(211, 84, 0, 0.1);
}

.dark-mode .feature h3 {
	color: #ecf0f1;
}

script:
function toggleTheme() {
	document.body.classList.toggle("dark-mode");
	const icon = document.getElementById("theme-icon");
	if (icon.classList.contains("fa-sun")) {
		icon.classList.remove("fa-sun");
		icon.classList.add("fa-moon");
	} else {
		icon.classList.remove("fa-moon");
		icon.classList.add("fa-sun");
	}
}
