style:
body {
	font-family: "Montserrat", sans-serif;
	background: #f0f4f8;
	color: #2c3e50;
	margin: 0;
	padding: 0;
	transition: background-color 0.3s, color 0.3s;
}

h1 {
	text-align: center;
	color: #3498db;
	padding: 40px 0;
	font-weight: 600;
}

.container {
	max-width: 1400px;
	margin: 0 auto;
	padding: 0 20px;
}

.features {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
	gap: 20px;
}

.feature {
	background: #ffffff;
	border: 1px solid #bdc3c7;
	border-radius: 5px;
	padding: 20px;
	position: relative;
	overflow: hidden;
	transition: all 0.3s ease;
}

.feature::before {
	content: "";
	position: absolute;
	top: 0;
	left: 0;
	width: 100%;
	height: 100%;
	background: #3498db;
	transform: scale(0);
	transition: transform 0.3s ease;
	opacity: 0.1;
	z-index: -1;
}

.feature:hover {
	border-color: #3498db;
	transform: translateY(-5px);
}

.feature:hover::before {
	transform: scale(1);
}

.feature i {
	font-size: 2em;
	color: #3498db;
	margin-bottom: 15px;
	transition: transform 0.3s ease;
}

.feature:hover i {
	transform: scale(1.2);
}

.feature h3 {
	margin: 10px 0 5px;
	color: #2c3e50;
	font-weight: 600;
}

.theme-toggle {
	position: fixed;
	top: 20px;
	right: 20px;
	background: #3498db;
	border: none;
	color: white;
	padding: 10px;
	border-radius: 50%;
	font-size: 24px;
	cursor: pointer;
	transition: transform 0.3s ease;
}

.theme-toggle:hover {
	transform: scale(1.1);
}

.dark-mode {
	background: #2c3e50;
	color: #ecf0f1;
}

.dark-mode .feature {
	background: #34495e;
	border-color: #4a627a;
}

.dark-mode .feature i {
	color: #2980b9;
}

.dark-mode .feature::before {
	background: #2980b9;
}

.dark-mode .feature h3 {
	color: #ecf0f1;
}

script:
function toggleTheme() {
	document.body.classList.toggle("dark-mode");
	const icon = document.getElementById("theme-icon");
	if (icon.classList.contains("fa-sun")) {
		icon.classList.remove("fa-sun");
		icon.classList.add("fa-moon");
	} else {
		icon.classList.remove("fa-moon");
		icon.classList.add("fa-sun");
	}
}
