style:
:root {
	--primary: #3b82f6;
	--secondary: #6366f1;
	--text: #1e293b;
	--bg: #ffffff;
	--card-bg: #f8fafc;
	--transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

[data-theme="dark"] {
	--primary: #60a5fa;
	--secondary: #818cf8;
	--text: #f1f5f9;
	--bg: #0f172a;
	--card-bg: #1e293b;
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Inter", system-ui, sans-serif;
}

body {
	background: var(--bg);
	color: var(--text);
	transition: var(--transition);
	line-height: 1.5;
}

.container {
	max-width: 1280px;
	margin: 0 auto;
	padding: 4rem 2rem;
}

.theme-btn {
	position: fixed;
	top: 1.5rem;
	right: 1.5rem;
	background: var(--card-bg);
	width: 48px;
	height: 48px;
	border-radius: 12px;
	border: none;
	cursor: pointer;
	display: grid;
	place-items: center;
	box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
}

.header {
	text-align: center;
	margin-bottom: 4rem;
	max-width: 800px;
	margin-left: auto;
	margin-right: auto;
}

.title {
	font-size: 2.5rem;
	margin-bottom: 1.5rem;
	background: linear-gradient(to right, var(--primary), var(--secondary));
	-webkit-background-clip: text;
	background-clip: text;
	-webkit-text-fill-color: transparent;
}

.subtitle {
	font-size: 1.1rem;
	opacity: 0.9;
}

.features-grid {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
	gap: 2rem;
}

.feature {
	background: var(--card-bg);
	padding: 2.5rem;
	border-radius: 16px;
	transition: var(--transition);
	position: relative;
	overflow: hidden;
}

.feature::before {
	content: "";
	position: absolute;
	top: -1px;
	left: -1px;
	right: -1px;
	bottom: -1px;
	border-radius: 16px;
	z-index: -1;
	background: linear-gradient(45deg, var(--primary), var(--secondary));
	opacity: 0;
	transition: opacity 0.3s ease;
}

.feature:hover {
	transform: translateY(-5px);
	box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.1);
}

.feature:hover::before {
	opacity: 0.1;
}

.feature-icon {
	width: 56px;
	height: 56px;
	background: linear-gradient(45deg, var(--primary), var(--secondary));
	border-radius: 12px;
	display: grid;
	place-items: center;
	margin-bottom: 1.5rem;
	color: white;
	font-size: 1.5rem;
}

.feature-title {
	font-size: 1.3rem;
	margin-bottom: 1rem;
}

.feature-desc {
	opacity: 0.9;
	font-size: 0.95rem;
	line-height: 1.6;
}

@media (max-width: 768px) {
	.container {
		padding: 2rem 1rem;
	}

	.title {
		font-size: 2rem;
	}

	.feature {
		padding: 2rem;
	}
}

script:
const themeButton = document.querySelector(".theme-btn");
const body = document.body;

// Theme management
const currentTheme = localStorage.getItem("theme") || "light";
body.setAttribute("data-theme", currentTheme);
updateThemeIcon(currentTheme);

themeButton.addEventListener("click", () => {
	const newTheme = body.getAttribute("data-theme") === "dark" ? "light" : "dark";
	body.setAttribute("data-theme", newTheme);
	localStorage.setItem("theme", newTheme);
	updateThemeIcon(newTheme);
});

function updateThemeIcon(theme) {
	themeButton.innerHTML =
		theme === "dark" ? '<i class="fas fa-moon"></i>' : '<i class="fas fa-desktop"></i>';
}
