style:
body {
	font-family: "Poppins", sans-serif;
	background: #f7f7f7;
	color: #333;
	margin: 0;
	padding: 0;
	transition: background-color 0.3s, color 0.3s;
}
h1 {
	text-align: center;
	color: #ff1493; /* Bright pink for a fashion vibe */
	padding: 40px 0;
	font-weight: 600;
	letter-spacing: 1px;
}
.container {
	max-width: 1200px;
	margin: 0 auto;
	padding: 0 20px;
}
.features {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
	gap: 20px;
}
.feature {
	background: #fff;
	border: 2px solid #ff1493;
	border-radius: 15px;
	padding: 20px;
	position: relative;
	transition: all 0.3s ease;
	overflow: hidden;
}
.feature::before {
	content: "";
	position: absolute;
	top: 0;
	left: -100%;
	width: 100%;
	height: 100%;
	background: rgba(255, 20, 147, 0.1);
	transform: skewX(-45deg);
	transition: left 0.5s;
}
.feature:hover {
	transform: translateY(-5px);
	box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1);
}
.feature:hover::before {
	left: 100%;
}
.feature i {
	font-size: 3em;
	color: #ff1493;
	margin-bottom: 15px;
	transition: transform 0.3s ease;
}
.feature:hover i {
	transform: scale(1.1);
}
.feature h3 {
	margin: 10px 0 5px;
	color: #333;
	font-weight: 600;
}
.theme-toggle {
	position: fixed;
	top: 20px;
	right: 20px;
	background: #ff1493;
	border: none;
	color: white;
	padding: 10px;
	border-radius: 50%;
	font-size: 24px;
	cursor: pointer;
	transition: transform 0.3s ease;
}
.theme-toggle:hover {
	transform: scale(1.1);
}
.dark-mode {
	background: #2c2c2c;
	color: #f7f7f7;
}
.dark-mode .feature {
	background: #3c3c3c;
	border-color: #ff69b4; /* A lighter pink for dark mode */
}
.dark-mode .feature i {
	color: #ff69b4;
}
.dark-mode .feature::before {
	background: rgba(255, 105, 180, 0.1);
}
.dark-mode .feature h3 {
	color: #f7f7f7;
}

script:
function toggleTheme() {
	document.body.classList.toggle("dark-mode");
	const icon = document.getElementById("theme-icon");
	if (icon.classList.contains("fa-sun")) {
		icon.classList.remove("fa-sun");
		icon.classList.add("fa-moon");
	} else {
		icon.classList.remove("fa-moon");
		icon.classList.add("fa-sun");
	}
}
