style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Inter", sans-serif;
}

.footer {
	background: linear-gradient(to right, #111827 50%, #1f2937 50%);
	color: white;
	padding: 4rem 0 2rem;
	position: relative;
	overflow: hidden;
}

.footer-container {
	max-width: 1200px;
	margin: 0 auto;
	padding: 0 2rem;
	display: grid;
	grid-template-columns: repeat(4, 1fr);
	gap: 2rem;
}

.footer-divider {
	position: absolute;
	left: 50%;
	top: 0;
	bottom: 0;
	width: 2px;
	background: rgba(255, 255, 255, 0.1);
}

.footer-section {
	padding: 1rem;
	position: relative;
}

.footer-section h6 {
	font-size: 1.1rem;
	margin-bottom: 1.5rem;
	padding-bottom: 0.5rem;
	position: relative;
	display: inline-block;
}

.footer-section h6:after {
	content: "";
	position: absolute;
	bottom: 0;
	left: 0;
	width: 40px;
	height: 2px;
	background: #3b82f6;
}

.footer-links {
	list-style: none;
}

.footer-links li {
	margin-bottom: 0.8rem;
}

.footer-links a {
	color: #9ca3af;
	text-decoration: none;
	position: relative;
	padding: 0.2rem 0;
	display: inline-block;
}

.footer-links a:before {
	content: "";
	position: absolute;
	bottom: 0;
	left: 0;
	width: 0;
	height: 1px;
	background: #3b82f6;
	transition: width 0.3s ease;
}

.footer-links a:hover:before {
	width: 100%;
}

.contact-info {
	margin-top: 1.5rem;
}

.contact-item {
	display: flex;
 justify-content:center;

	align-items: center;
	gap: 0.8rem;
	margin-bottom: 1rem;
	color: #9ca3af;
}

.social-links {
	display: flex;
	gap: 1rem;
	margin-top: 2rem;
}

.social-link {
	width: 40px;
	height: 40px;
	border: 1px solid rgba(255, 255, 255, 0.1);
	border-radius: 50%;
	display: flex;
	align-items: center;
	justify-content: center;
	color: white;
	transition: all 0.3s ease;
}

.social-link:hover {
	background: #3b82f6;
	transform: translateY(-3px);
}

.newsletter-form {
	margin-top: 1.5rem;
	position: relative;
}

.newsletter-input {
	width: 100%;
	padding: 0.8rem;
	background: rgba(255, 255, 255, 0.05);
	border: 1px solid rgba(255, 255, 255, 0.1);
	color: white;
	border-radius: 6px;
}

.newsletter-button {
	position: absolute;
	right: 5px;
	top: 5px;
	bottom: 5px;
	padding: 0 1rem;
	background: #3b82f6;
	color: white;
	border: none;
	border-radius: 4px;
	cursor: pointer;
	transition: background 0.3s ease;
}

.newsletter-button:hover {
	background: #2563eb;
}

.footer-bottom {
	margin-top: 3rem;
	padding-top: 2rem;
	border-top: 1px solid rgba(255, 255, 255, 0.1);
	grid-column: 1 / -1;
	display: flex;
	justify-content: space-between;
	flex-wrap: wrap;
	gap: 1rem;
}

.legal-links {
	display: flex;
	gap: 1.5rem;
}

.legal-links a {
	color: #9ca3af;
	text-decoration: none;
	transition: color 0.3s ease;
}

.legal-links a:hover {
	color: #3b82f6;
}

@media (max-width: 1024px) {
	.footer-container {
		grid-template-columns: repeat(2, 1fr);
	}
}

@media (max-width: 768px) {
	.footer {
		background: #111827;
	}
	.footer-container {
		grid-template-columns: 1fr;
	}
	.footer-divider {
		display: none;
	}
	.footer-section {
		text-align: center;
	}
	.footer-section h6:after {
		left: 50%;
		transform: translateX(-50%);
	}
	.social-links {
		justify-content: center;
	}
	.footer-bottom {
		flex-direction: column;
		text-align: center;
	}
	.legal-links {
		justify-content: center;
	}
}

script:
// Newsletter form handling
document.querySelector(".newsletter-form").addEventListener("submit", (e) => {
	e.preventDefault();
	const email = e.target.querySelector("input").value;
	if (validateEmail(email)) {
		showToast("Thank you for subscribing!");
		e.target.reset();
	} else {
		showToast("Please enter a valid email", "error");
	}
});

function validateEmail(email) {
	const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
	return re.test(email);
}

function showToast(message, type = "success") {
	const toast = document.createElement("div");
	toast.textContent = message;
	toast.style.position = "fixed";
	toast.style.bottom = "20px";
	toast.style.left = "50%";
	toast.style.transform = "translateX(-50%)";
	toast.style.padding = "1rem 2rem";
	toast.style.borderRadius = "8px";
	toast.style.background = type === "error" ? "#ef4444" : "#3b82f6";
	toast.style.color = "white";
	toast.style.zIndex = "1000";
	document.body.appendChild(toast);

	setTimeout(() => {
		toast.remove();
	}, 3000);
}
