style:
:root {
	--primary: #7f5af0;
	--secondary: #2cb67d;
	--bg: #16161a;
	--text: #fffffe;
	--card-bg: rgba(255, 255, 255, 0.05);
	--transition: all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
}

[data-theme="light"] {
	--bg: #fffffe;
	--text: #16161a;
	--card-bg: rgba(22, 22, 26, 0.05);
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
}

body {
	font-family: "Inter", sans-serif;
	background: var(--bg);
	color: var(--text);
	min-height: 200vh;
}

.footer {
	position: relative;
	padding: 6rem 0 4rem;
	overflow: hidden;
}

.fluid-gradient {
	position: absolute;
	width: 200%;
	height: 150%;
	background: linear-gradient(45deg, var(--primary), var(--secondary), var(--primary));
	opacity: 0.1;
	filter: blur(80px);
	animation: gradientFlow 20s linear infinite;
	z-index: -1;
}

.footer-container {
	max-width: 1200px;
	margin: 0 auto;
	padding: 0 2rem;
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
	gap: 2rem;
}

.footer-section {
	padding: 2rem;
	background: var(--card-bg);
	backdrop-filter: blur(12px);
	border-radius: 1.5rem;
	transform: translateY(20px);
	opacity: 0;
	transition: var(--transition);
	position: relative;
	overflow: hidden;
}

.footer-section::before {
	content: "";
	position: absolute;
	top: 0;
	left: -100%;
	width: 50%;
	height: 100%;
	background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.1), transparent);
	transition: var(--transition);
}

.footer-section:hover::before {
	left: 100%;
}

.section-title {
	font-size: 1.25rem;
	margin-bottom: 1.5rem;
	position: relative;
	display: inline-block;
}

.section-title::after {
	content: "";
	position: absolute;
	bottom: -8px;
	left: 0;
	width: 40px;
	height: 3px;
	background: linear-gradient(90deg, var(--primary), var(--secondary));
}

.footer-links {
	list-style: none;
}

.footer-links li {
	margin-bottom: 1rem;
}

.footer-links a {
	color: var(--text);
	text-decoration: none;
	display: flex;
	align-items: center;
	gap: 0.8rem;
	padding: 0.75rem;
	border-radius: 0.75rem;
	transition: var(--transition);
}

.footer-links a:hover {
	background: rgba(255, 255, 255, 0.05);
	transform: translateX(10px);
}

.newsletter-form {
	margin-top: 2rem;
	position: relative;
}

.newsletter-input {
	width: 100%;
	padding: 1rem;
	background: rgba(255, 255, 255, 0.05);
	border: none;
	border-radius: 0.75rem;
	color: var(--text);
	transition: var(--transition);
}

.newsletter-input:focus {
	outline: 2px solid var(--primary);
	outline-offset: 2px;
}

.newsletter-button {
	width: 100%;
	padding: 1rem;
	margin-top: 1rem;
	background: linear-gradient(45deg, var(--primary), var(--secondary));
	border: none;
	border-radius: 0.75rem;
	color: white;
	cursor: pointer;
	transition: var(--transition);
}

.newsletter-button:hover {
	transform: translateY(-3px);
	box-shadow: 0 8px 20px rgba(0, 0, 0, 0.2);
}

.theme-toggle {
	position: fixed;
	bottom: 2rem;
	right: 2rem;
	width: 3.5rem;
	height: 3.5rem;
	border-radius: 0.75rem;
	background: linear-gradient(45deg, var(--primary), var(--secondary));
	border: none;
	cursor: pointer;
	display: flex;
	align-items: center;
	justify-content: center;
	color: white;
	z-index: 1000;
	transition: var(--transition);
}

@keyframes gradientFlow {
	0% {
		transform: rotate(0deg);
	}
	100% {
		transform: rotate(360deg);
	}
}

@media (max-width: 768px) {
	.footer-container {
		grid-template-columns: 1fr;
	}

	.footer-section {
		text-align: center;
	}

	.section-title::after {
		left: 50%;
		transform: translateX(-50%);
	}

	.footer-links a {
		justify-content: center;
	}
}

script:
// Theme Toggle
const themeToggle = document.getElementById("themeToggle");
let isDark = true;

themeToggle.addEventListener("click", () => {
	isDark = !isDark;
	document.documentElement.setAttribute("data-theme", isDark ? "" : "light");

themeToggle.innerHTML = isDark ? '<i class="fas fa-moon"></i>' : '<i class="fas fa-sun"></i>';
});

// Intersection Observer
const observer = new IntersectionObserver(
	(entries) => {
		entries.forEach((entry) => {
			if (entry.isIntersecting) {
				entry.target.style.opacity = 1;
				entry.target.style.transform = "translateY(0)";
			}
		});
	},
	{ threshold: 0.1 },
);

document.querySelectorAll(".footer-section").forEach((section) => {
	observer.observe(section);
});

// Form Validation
document.getElementById("newsletterForm").addEventListener("submit", (e) => {
	e.preventDefault();
	const email = e.target.querySelector("input").value;
	if (validateEmail(email)) {
		showNotification("🎉 Subscription successful!");
		e.target.reset();
	} else {
		showNotification("⚠️ Please enter a valid email");
	}
});

function validateEmail(email) {
	return /^[^\s@]+@[\s@]+\.[\s@]+$/.test(email);
}

function showNotification(message) {
	const notification = document.createElement("div");
	notification.textContent = message;
	notification.style.position = "fixed";
	notification.style.bottom = "6rem";
	notification.style.right = "2rem";
	notification.style.padding = "1rem 2rem";
	notification.style.background = "var(--card-bg)";
	notification.style.color = "var(--text)";
	notification.style.borderRadius = "0.75rem";
	notification.style.backdropFilter = "blur(12px)";
	notification.style.border = "1px solid rgba(255, 255, 255, 0.1)";
	document.body.appendChild(notification);

	setTimeout(() => notification.remove(), 3000);
}
