style:
:root {
	--bg: #ffffff;
	--text: #1f2937;
	--primary: #3b82f6;
	--secondary: #64748b;
	--border: #e2e8f0;
	--footer-bg: #f8fafc;
	--input-bg: #ffffff;
}

[data-theme="dark"] {
	--bg: #0f172a;
	--text: #f8fafc;
	--primary: #60a5fa;
	--secondary: #94a3b8;
	--border: #334155;
	--footer-bg: #1e293b;
	--input-bg: #1e293b;
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	transition: all 0.3s ease;
}

body {
	background: var(--bg);
	color: var(--text);
	font-family: "Inter", sans-serif;
	min-height: 100vh;
	display: flex;
	flex-direction: column;
}

main {
	flex: 1;
	padding: 2rem;
}

.footer {
	background: var(--footer-bg);
	border-top: 1px solid var(--border);
	margin-top: auto;
}

.footer-container {
	max-width: 1440px;
	margin: 0 auto;
	padding: 4rem 2rem 2rem;
	display: grid;
	grid-template-columns: repeat(5, 1fr);
	gap: 2rem;
}

.footer-section {
	padding: 0 1rem;
}

.section-title {
	color: var(--primary);
	font-size: 1.1rem;
	margin-bottom: 1.5rem;
	position: relative;
	padding-bottom: 0.5rem;
}

.section-title::after {
	content: "";
	position: absolute;
	bottom: 0;
	left: 0;
	width: 40px;
	height: 2px;
	background: var(--primary);
}

.footer-links {
	list-style: none;
	display: grid;
	gap: 0.8rem;
}

.footer-links a {
	color: var(--secondary);
	text-decoration: none;
	display: flex;
	align-items: center;
	gap: 0.5rem;
	padding: 0.5rem;
	border-radius: 4px;
	transition: all 0.2s ease;
}

.footer-links a:hover {
	background: rgba(59, 130, 246, 0.1);
	color: var(--primary);
	transform: translateX(5px);
}

.map-container {
	height: 200px;
	background: var(--bg);
	border-radius: 8px;
	border: 1px solid var(--border);
	overflow: hidden;
	position: relative;
}

.map-placeholder {
	display: flex;
	align-items: center;
	justify-content: center;
	height: 100%;
	color: var(--secondary);
}

.newsletter-form {
	display: grid;
	gap: 1rem;
}

.input-group {
	position: relative;
}

.newsletter-input {
	width: 100%;
	padding: 0.75rem 1rem;
	border: 1px solid var(--border);
	border-radius: 6px;
	background: var(--input-bg);
	color: var(--text);
}

.newsletter-input:focus {
	outline: none;
	border-color: var(--primary);
	box-shadow: 0 0 0 2px rgba(59, 130, 246, 0.2);
}

.footer-bottom {
	border-top: 1px solid var(--border);
	padding: 2rem;
	margin-top: 2rem;
	display: flex;
	justify-content: space-between;
	align-items: center;
}

.theme-switch {
	display: flex;
	align-items: center;
	gap: 1rem;
}

.theme-toggle {
	width: 48px;
	height: 24px;
	background: var(--secondary);
	border-radius: 12px;
	position: relative;
	cursor: pointer;
}

.theme-toggle::after {
	content: "";
	position: absolute;
	top: 2px;
	left: 2px;
	width: 20px;
	height: 20px;
	background: var(--bg);
	border-radius: 50%;
	transition: transform 0.3s;
}

[data-theme="dark"] .theme-toggle::after {
	transform: translateX(24px);
}

.back-to-top {
	background: var(--primary);
	color: white;
	padding: 0.75rem 1.5rem;
	border-radius: 6px;
	text-decoration: none;
	display: flex;
	align-items: center;
	gap: 0.5rem;
}

@media (max-width: 1024px) {
	.footer-container {
		grid-template-columns: repeat(3, 1fr);
	}
}

@media (max-width: 768px) {
	.footer-container {
		grid-template-columns: 1fr;
	}

	.footer-section {
		padding: 0;
	}

	.section-title {
		cursor: pointer;
	}

	.footer-links {
		max-height: 0;
		overflow: hidden;
		transition: max-height 0.3s ease-out;
	}

	.footer-section.active .footer-links {
		max-height: 1000px;
	}
}

script:
// Theme Management
let isDark =
	localStorage.getItem("theme") === "dark" ||
	(window.matchMedia("(prefers-color-scheme: dark)").matches && !localStorage.getItem("theme"));

function toggleTheme() {
	isDark = !isDark;
	document.documentElement.setAttribute("data-theme", isDark ? "dark" : "light");
	localStorage.setItem("theme", isDark ? "dark" : "light");
}

// Initialize theme
document.documentElement.setAttribute("data-theme", isDark ? "dark" : "light");

// Newsletter Validation
const newsletterForm = document.getElementById("newsletterForm");
newsletterForm.addEventListener("submit", (e) => {
	e.preventDefault();
	const email = newsletterForm.querySelector("input").value;
	if (validateEmail(email)) {
		showSuccess();
		newsletterForm.reset();
	} else {
		showError();
	}
});

function validateEmail(email) {
	return /^[^s@]+@[^s@]+\.[^s@]+$/.test(email);
}

function showSuccess() {
	const btn = newsletterForm.querySelector("button");
	btn.innerHTML = '<i class="fas fa-check"></i> Subscribed!';
	btn.style.backgroundColor = "#10b981";
	setTimeout(() => {
		btn.innerHTML = '<i class="fas fa-envelope"></i> Subscribe';
		btn.style.backgroundColor = "";
	}, 2000);
}

function showError() {
	const input = newsletterForm.querySelector("input");
	input.style.borderColor = "#ef4444";
}

// Scroll to Top
function scrollToTop() {
	window.scrollTo({ top: 0, behavior: "smooth" });
}

// Dynamic Year
document.getElementById("currentYear").textContent = new Date().getFullYear();
