style:
:root {
	--neon-blue: #00f3ff;
	--neon-pink: #ff00ff;
	--bg: #000;
	--text: #fff;
	--grid-line: rgba(0, 243, 255, 0.1);
	--glow: 0 0 15px var(--neon-blue);
}

[data-theme="light"] {
	--bg: #f0f0f0;
	--text: #000;
	--grid-line: rgba(0, 0, 0, 0.1);
	--glow: 0 0 15px rgba(0, 0, 0, 0.2);
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Courier New", monospace;
}

body {
	background: var(--bg);
	color: var(--text);
	min-height: 200vh;
	position: relative;
}

.grid-lines {
	position: absolute;
	width: 100%;
	height: 100%;
	background-image: linear-gradient(var(--grid-line) 1px, transparent 1px),
		linear-gradient(90deg, var(--grid-line) 1px, transparent 1px);
	background-size: 20px 20px;
	pointer-events: none;
}

.footer {
	padding: 4rem 0;
	border-top: 2px solid var(--neon-blue);
	position: relative;
	overflow: hidden;
}

.footer::before {
	content: "";
	position: absolute;
	top: -2px;
	left: 0;
	width: 100%;
	height: 2px;
	background: linear-gradient(90deg, var(--neon-blue), var(--neon-pink));
	filter: drop-shadow(var(--glow));
}

.footer-container {
	max-width: 1200px;
	margin: 0 auto;
	padding: 0 2rem;
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
	gap: 2rem;
	position: relative;
}

.footer-section {
	padding: 2rem;
	background: rgba(0, 0, 0, 0.3);
	border: 1px solid var(--neon-blue);
	clip-path: polygon(0 0, 100% 0, 100% 80%, 90% 100%, 0 100%);
	transform: translateY(20px);
	opacity: 0;
}

[data-theme="light"] .footer-section {
	background: rgba(255, 255, 255, 0.3);
}

.footer-title {
	font-size: 1.5rem;
	margin-bottom: 1.5rem;
	text-transform: uppercase;
	position: relative;
	color: var(--neon-pink);
	text-shadow: 0 0 10px var(--neon-pink);
}

.footer-title::after {
	content: "";
	position: absolute;
	bottom: -8px;
	left: 0;
	width: 50px;
	height: 2px;
	background: var(--neon-blue);
}

.footer-links {
	list-style: none;
}

.footer-links li {
	margin-bottom: 1rem;
}

.footer-links a {
	color: var(--text);
	text-decoration: none;
	padding: 0.5rem;
	display: block;
	position: relative;
	transition: all 0.3s ease;
}

.footer-links a::before {
	content: ">";
	color: var(--neon-blue);
	margin-right: 0.5rem;
}

.footer-links a:hover {
	color: var(--neon-pink);
	transform: translateX(10px);
}

.social-grid {
	display: grid;
	grid-template-columns: repeat(3, 1fr);
	gap: 1rem;
	margin-top: 2rem;
}

.social-link {
	padding: 1rem;
	border: 1px solid var(--neon-blue);
	text-align: center;
	transition: all 0.3s ease;
	clip-path: polygon(0 0, 100% 0, 100% 75%, 90% 100%, 0 100%);
}

.social-link:hover {
	background: var(--neon-blue);
	color: #000;
	transform: translateY(-5px);
}

.newsletter-form {
	margin-top: 2rem;
	position: relative;
}

.newsletter-input {
	width: 100%;
	padding: 1rem;
	background: transparent;
	border: 1px solid var(--neon-blue);
	color: var(--text);
	margin-bottom: 1rem;
}

.newsletter-button {
	width: 100%;
	padding: 1rem;
	background: var(--neon-pink);
	border: none;
	color: #000;
	font-weight: bold;
	cursor: pointer;
	clip-path: polygon(0 0, 100% 0, 100% 70%, 90% 100%, 0 100%);
	transition: all 0.3s ease;
}

.newsletter-button:hover {
	background: var(--neon-blue);
}

.theme-toggle {
	position: fixed;
	bottom: 30px;
	right: 30px;
	width: 60px;
	height: 60px;
	background: var(--neon-pink);
	border: 1px solid var(--neon-blue);
	clip-path: polygon(0 0, 100% 0, 100% 70%, 90% 100%, 0 100%);
	cursor: pointer;
	color: #000;
	display: flex;
	align-items: center;
	justify-content: center;
	transition: all 0.3s ease;
}

.theme-toggle:hover {
	clip-path: polygon(0 0, 100% 0, 100% 100%, 0 100%);
}

@media (max-width: 768px) {
	.footer-container {
		grid-template-columns: 1fr;
	}

	.footer-section {
		clip-path: none;
	}
}

.scanline {
	position: absolute;
	width: 100%;
	height: 100%;
	background: linear-gradient(to bottom, transparent 50%, rgba(0, 243, 255, 0.1) 51%, transparent 52%);
	animation: scan 4s linear infinite;
	pointer-events: none;
}

@keyframes scan {
	0% {
		transform: translateY(-100%);
	}
	100% {
		transform: translateY(100%);
	}
}

script:
const themeToggle = document.getElementById("themeToggle");
const body = document.body;

themeToggle.addEventListener("click", () => {
	body.setAttribute("data-theme", body.getAttribute("data-theme") === "light" ? "" : "light");
});

const observer = new IntersectionObserver((entries) => {
	entries.forEach((entry) => {
		if (entry.isIntersecting) {
			entry.target.style.opacity = 1;
			entry.target.style.transform = "translateY(0)";
		}
	});
});

document.querySelectorAll(".footer-section").forEach((section) => {
	observer.observe(section);
});

document.getElementById("newsletterForm").addEventListener("submit", (e) => {
	e.preventDefault();
	const email = e.target.querySelector("input").value;
	if (validateEmail(email)) {
		showTerminalMessage("DATASTREAM SUBSCRIBED");
		e.target.reset();
	} else {
		showTerminalMessage("ERROR: INVALID NEURAL ID");
	}
});

function validateEmail(email) {
	const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
	return re.test(email);
}

function showTerminalMessage(message) {
	const terminal = document.createElement("div");
	terminal.textContent = `> ${message}`;
	terminal.style.position = "fixed";
	terminal.style.bottom = "100px";
	terminal.style.right = "30px";
	terminal.style.padding = "1rem 2rem";
	terminal.style.background = "#000";
	terminal.style.border = "1px solid var(--neon-blue)";
	terminal.style.color = "var(--neon-blue)";
	terminal.style.fontFamily = "monospace";
	terminal.style.zIndex = "1000";
	document.body.appendChild(terminal);

	setTimeout(() => {
		terminal.remove();
	}, 3000);
}
