style:
:root {
	--primary: hsl(210, 100%, 60%);
	--secondary: hsl(270, 100%, 60%);
	--bg: #0a0a12;
	--text: #ffffff;
	--card-bg: rgba(255, 255, 255, 0.05);
	--border: rgba(255, 255, 255, 0.1);
}

[data-theme="light"] {
	--bg: #f8f9fa;
	--text: #212529;
	--card-bg: rgba(0, 0, 0, 0.03);
	--border: rgba(0, 0, 0, 0.1);
}

*
{
	margin: 0;
	padding: 0;
	box-sizing: border-box;
}

body {
	font-family: "Inter", sans-serif;
	background: var(--bg);
	color: var(--text);
	min-height: 200vh;
}

.footer {
	position: relative;
	padding: 6rem 0 3rem;
	overflow: hidden;
}

.footer::before {
	content: "";
	position: absolute;
	top: -50px;
	left: 0;
	width: 100%;
	height: 100px;
	background: var(--bg);
	transform: skewY(-3deg);
	z-index: 1;
}

.footer-container {
	max-width: 1200px;
	margin: 0 auto;
	padding: 0 2rem;
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
	gap: 2rem;
	position: relative;
	z-index: 2;
}

.footer-card {
	padding: 2rem;
	background: var(--card-bg);
	border-radius: 1.5rem;
	border: 1px solid var(--border);
	backdrop-filter: blur(12px);
	transform-style: preserve-3d;
	transition: transform 0.3s ease;
	position: relative;
	overflow: hidden;
}

.footer-card::after {
	content: "";
	position: absolute;
	top: -50%;
	left: -50%;
	width: 200%;
	height: 200%;
	background: linear-gradient(45deg, transparent 45%, var(--primary) 50%, transparent 55%);
	animation: gradientFlow 4s linear infinite;
	opacity: 0.1;
	z-index: -1;
}

.card-title {
	font-size: 1.25rem;
	margin-bottom: 1.5rem;
	position: relative;
	display: inline-block;
}

.card-title::after {
	content: "";
	position: absolute;
	bottom: -8px;
	left: 0;
	width: 40px;
	height: 2px;
	background: linear-gradient(90deg, var(--primary), var(--secondary));
}

.footer-links {
	list-style: none;
}

.footer-links li {
	margin-bottom: 1rem;
}

.footer-links a {
	color: var(--text);
	text-decoration: none;
	display: flex;
	align-items: center;
	gap: 0.8rem;
	padding: 0.5rem;
	border-radius: 0.5rem;
	transition: all 0.3s ease;
}

.footer-links a:hover {
	background: rgba(255, 255, 255, 0.05);
	transform: translateX(8px);
}

.newsletter-form {
	margin-top: 2rem;
	position: relative;
}

.newsletter-input {
	width: 100%;
	padding: 1rem;
	background: rgba(255, 255, 255, 0.05);
	border: 1px solid var(--border);
	border-radius: 0.75rem;
	color: var(--text);
	transition: all 0.3s ease;
}

.newsletter-input:focus {
	outline: none;
	box-shadow: 0 0 0 2px var(--primary);
}

.newsletter-button {
	width: 100%;
	padding: 1rem;
	margin-top: 1rem;
	background: linear-gradient(45deg, var(--primary), var(--secondary));
	border: none;
	border-radius: 0.75rem;
	color: white;
	cursor: pointer;
	transition: transform 0.3s ease;
}

.newsletter-button:hover {
	transform: translateY(-2px);
}

.theme-toggle {
	position: fixed;
	bottom: 2rem;
	right: 2rem;
	width: 3.5rem;
	height: 3.5rem;
	border-radius: 50%;
	background: linear-gradient(45deg, var(--primary), var(--secondary));
	border: none;
	cursor: pointer;
	display: flex;
	align-items: center;
	justify-content: center;
	color: white;
	z-index: 1000;
	box-shadow: 0 4px 15px rgba(0, 0, 0, 0.2);
}

@keyframes gradientFlow {
	0% {
		transform: rotate(0deg) translateX(-25%);
	}
	100% {
		transform: rotate(360deg) translateX(-25%);
	}
}

@media (max-width: 768px) {
	.footer-container {
		grid-template-columns: 1fr;
	}

	.footer-card {
		text-align: center;
	}

	.card-title::after {
		left: 50%;
		transform: translateX(-50%);
	}

	.footer-links a {
		justify-content: center;
	}
}

script:
// Theme Toggle
const themeToggle = document.getElementById("themeToggle");
let isDark = true;

themeToggle.addEventListener("click", () => {
	isDark = !isDark;
	document.documentElement.setAttribute("data-theme", isDark ? "" : "light");

themeToggle.innerHTML = isDark ? '<i class="fas fa-moon"></i>' : '<i class="fas fa-sun"></i>';
});

// Parallax Tilt Effect
document.querySelectorAll(".footer-card").forEach((card) => {
	card.addEventListener("mousemove", (e) => {
		const rect = card.getBoundingClientRect();
		const x = e.clientX - rect.left;
		const y = e.clientY - rect.top;
		const centerX = rect.width / 2;
		const centerY = rect.height / 2;

		card.style.transform = `
		perspective(1000px)
		rotateX(${(centerY - y) / 15}deg)
		rotateY(${(x - centerX) / 15}deg)
	`;
	});

	card.addEventListener("mouseleave", () => {
		card.style.transform = "perspective(1000px) rotateX(0) rotateY(0)";
	});
});

// Form Validation
document.getElementById("newsletterForm").addEventListener("submit", (e) => {
	e.preventDefault();
	const email = e.target.querySelector("input").value;
	if (validateEmail(email)) {
		showNotification("Subscription successful! 🎉");
		e.target.reset();
	} else {
		showNotification("Please enter a valid email ⚠️");
	}
});

function validateEmail(email) {
	return /^[^s@]+@[^s@]+.[^s@]+$/.test(email);
}

function showNotification(message) {
	const notification = document.createElement("div");
	notification.textContent = message;
	notification.style.position = "fixed";
	notification.style.bottom = "6rem";
	notification.style.right = "2rem";
	notification.style.padding = "1rem 2rem";
	notification.style.background = "var(--card-bg)";
	notification.style.color = "var(--text)";
	notification.style.borderRadius = "0.75rem";
	notification.style.backdropFilter = "blur(12px)";
	notification.style.border = "1px solid var(--border)";

document.body.appendChild(notification);

	setTimeout(() => notification.remove(), 3000);
}
