style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Inter", sans-serif;
}

.footer {
	background: #0f172a;
	color: #ffffff;
	padding: 60px 0 0;
	margin-top: 80px;
}

.footer-container {
	max-width: 1280px;
	margin: 0 auto;
	padding: 0 32px;
	display: flex;
	flex-wrap: wrap;
	gap: 40px;
}

.footer-column {
	flex: 1 1 300px;
	padding: 20px;
}

.brand-section {
	max-width: 350px;
}

.logo {
	width: 160px;
	margin-bottom: 25px;
}

.brand-text {
	color: #94a3b8;
	line-height: 1.8;
	margin-bottom: 25px;
}

.social-links {
	display: flex;
	gap: 15px;
}

.social-links a {
	width: 40px;
	height: 40px;
	background: #1e293b;
	border-radius: 8px;
	display: flex;
	align-items: center;
	justify-content: center;
	transition: all 0.3s ease;
}

.social-links a:hover {
	background: #3b82f6;
	transform: translateY(-3px);
}

.footer-title {
	font-size: 1.1rem;
	color: #ffffff;
	margin-bottom: 25px;
	position: relative;
	padding-bottom: 10px;
}

.footer-title::after {
	content: "";
	position: absolute;
	bottom: 0;
	left: 0;
	width: 40px;
	height: 2px;
	background: #3b82f6;
}

.footer-links {
	list-style: none;
}

.footer-links li {
	margin-bottom: 15px;
}

.footer-links a {
	color: #94a3b8;
	text-decoration: none;
	transition: all 0.3s ease;
	display: flex;
	align-items: center;
	gap: 10px;
}

.footer-links a:hover {
	color: #3b82f6;
	transform: translateX(5px);
}

.gallery-grid {
	display: grid;
	grid-template-columns: repeat(3, 1fr);
	gap: 10px;
	margin-top: 15px;
}

.gallery-item {
	aspect-ratio: 1;
	overflow: hidden;
	border-radius: 8px;
	transition: transform 0.3s ease;
}

.gallery-item:hover {
	transform: scale(1.05);
}

.gallery-item img {
	width: 100%;
	height: 100%;
	object-fit: cover;
}

.newsletter-form {
	margin-top: 25px;
}

.newsletter-input {
	width: 100%;
	padding: 12px 20px;
	background: #1e293b;
	border: 1px solid #334155;
	border-radius: 8px;
	color: #ffffff;
	margin-bottom: 15px;
}

.newsletter-input:focus {
	outline: none;
	border-color: #3b82f6;
}

.newsletter-button {
	background: #3b82f6;
	color: #ffffff;
	padding: 12px 25px;
	border: none;
	border-radius: 8px;
	cursor: pointer;
	transition: all 0.3s ease;
	display: flex;
	align-items: center;
	gap: 10px;
}

.newsletter-button:hover {
	background: #2563eb;
	transform: translateY(-2px);
}

.footer-bottom {
	background: #1e293b;
	margin-top: 60px;
	padding: 25px 0;
	text-align: center;
}

.footer-bottom-text {
	color: #94a3b8;
	font-size: 0.9rem;
}

@media (max-width: 768px) {
	.footer-container {
		flex-direction: column;
		text-align: center;
	}

	.social-links {
		justify-content: center;
	}

	.footer-title::after {
		left: 50%;
		transform: translateX(-50%);
	}

	.footer-links a {
		justify-content: center;
	}

	.newsletter-button {
		justify-content: center;
	}
}

@keyframes fadeIn {
	from {
		opacity: 0;
		transform: translateY(20px);
	}

to {
		opacity: 1;
		transform: translateY(0);
	}
}

.animate-fade-in {
	animation: fadeIn 0.8s ease forwards;
}

script:
document.getElementById("newsletterForm").addEventListener("submit", function (e) {
	e.preventDefault();
	const email = this.querySelector("input").value;
	if (validateEmail(email)) {
		alert("Thank you for subscribing!");
		this.reset();
	} else {
		alert("Please enter a valid email address");
	}
});

function validateEmail(email) {
	const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
	return re.test(email);
}

const observerOptions = {
	threshold: 0.1,
};

const observer = new IntersectionObserver((entries) => {
	entries.forEach((entry) => {
		if (entry.isIntersecting) {
			entry.target.classList.add("animate-fade-in");
		}
	});
}, observerOptions);

document.querySelectorAll(".animate-fade-in").forEach((element) => {
	observer.observe(element);
});
