style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: 'Inter', sans-serif;
}

:root {
	--primary: #2563eb;
	--secondary: #1f2937;
	--accent: #3b82f6;
}

body {
	overflow-x: hidden;
}

.hero {
	min-height: 100vh;
	padding: 100px 5% 60px;
	background: linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5)),
		url('https://images.unsplash.com/photo-1502877338535-766e1452684a?ixlib=rb-1.2.1&auto=format&fit=crop&w=1920&q=80');
	background-size: cover;
	background-position: center;
	display: flex;
	align-items: center;
}

.hero-content {
	max-width: 1200px;
	margin: 0 auto;
	width: 100%;
}

.hero-text {
	text-align: center;
	color: white;
	margin-bottom: 3rem;
}

.hero-title {
	font-size: clamp(2rem, 5vw, 3.5rem);
	line-height: 1.1;
	margin-bottom: 1rem;
}

.search-form {
	background: white;
	padding: 2rem;
	border-radius: 1rem;
	max-width: 800px;
	margin: 0 auto 3rem;
	box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
}

.form-grid {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
	gap: 1.5rem;
	margin-bottom: 1.5rem;
}

.form-group {
	position: relative;
}

.form-label {
	display: block;
	text-align: left;
	color: var(--secondary);
	margin-bottom: 0.5rem;
	font-weight: 500;
}

.form-input {
	width: 100%;
	padding: 0.75rem 1rem;
	border: 1px solid #e5e7eb;
	border-radius: 0.5rem;
	font-size: 1rem;
}

.form-input:focus {
	outline: none;
	border-color: var(--primary);
	box-shadow: 0 0 0 3px rgba(37, 99, 235, 0.1);
}

.search-button {
	width: 100%;
	padding: 1rem;
	background: var(--primary);
	color: white;
	border: none;
	border-radius: 0.5rem;
	font-size: 1rem;
	font-weight: 600;
	cursor: pointer;
	transition: transform 0.3s ease;
}

.search-button:hover {
	transform: translateY(-2px);
}

.usp-grid {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
	gap: 2rem;
	max-width: 1000px;
	margin: 0 auto;
}

.usp-card {
	display: flex;
	align-items: center;
	gap: 1rem;
	padding: 1.5rem;
	background: rgba(255, 255, 255, 0.1);
	border-radius: 0.5rem;
	backdrop-filter: blur(5px);
	color: white;
}

.usp-icon {
	width: 50px;
	height: 50px;
	background: var(--primary);
	border-radius: 50%;
	display: grid;
	place-items: center;
	font-size: 1.25rem;
}

@media (max-width: 768px) {
	.search-form {
		padding: 1.5rem;
	}

	.form-grid {
		grid-template-columns: 1fr;
	}

	.usp-card {
		flex-direction: column;
		text-align: center;
	}
}
