style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Arial", sans-serif;
}

body {
	background: #f9f9f9;
}

.hero {
	background: linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5)),
		url("https://picsum.photos/1920/1080?grayscale");
	background-size: cover;
	background-position: center;
	padding: 100px 5%;
	color: white;
	text-align: center;
}

.hero-content {
	max-width: 800px;
	margin: 0 auto;
}

.hero-content h1 {
	font-size: 3rem;
	margin-bottom: 20px;
}

.hero-content p {
	font-size: 1.2rem;
	margin-bottom: 40px;
}

.search-bar {
	display: flex;
	justify-content: center;
	margin-bottom: 40px;
}

.search-bar input {
	width: 70%;
	padding: 15px;
	border: none;
	border-radius: 5px 0 0 5px;
	font-size: 1rem;
}

.search-bar button {
	padding: 15px 30px;
	border: none;
	background: #4caf50;
	color: white;
	border-radius: 0 5px 5px 0;
	cursor: pointer;
}

.features {
	display: flex;
	justify-content: center;
	gap: 40px;
	flex-wrap: wrap;
}

.feature-item {
	text-align: center;
	max-width: 200px;
}

.feature-item i {
	font-size: 2rem;
	margin-bottom: 10px;
	color: #4caf50;
}

.feature-item h3 {
	font-size: 1.2rem;
	margin-bottom: 10px;
}

.feature-item p {
	font-size: 0.9rem;
	color: #ddd;
}

.categories {
	padding: 60px 5%;
	background: #fff;
	text-align: center;
}

.categories h2 {
	font-size: 2rem;
	margin-bottom: 40px;
	color: #2c3e50;
}

.category-grid {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
	gap: 20px;
}

.category-card {
	background: #f9f9f9;
	padding: 20px;
	border-radius: 10px;
	text-align: center;
	transition: transform 0.3s ease;
}

.category-card:hover {
	transform: translateY(-10px);
}

.category-card i {
	font-size: 2rem;
	color: #4caf50;
	margin-bottom: 15px;
}

.category-card h3 {
	font-size: 1.2rem;
	color: #2c3e50;
}

@media (max-width: 768px) {
	.hero-content h1 {
		font-size: 2rem;
	}

	.hero-content p {
		font-size: 1rem;
	}

	.search-bar input {
		width: 60%;
	}

	.features {
		gap: 20px;
	}
}

@media (max-width: 480px) {
	.search-bar input {
		width: 50%;
	}
}
