style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Segoe UI", sans-serif;
}

.hero {
	min-height: 100vh;
	display: grid;
	grid-template-columns: 1fr 1fr;
	align-items: center;
	padding: 2rem;
	background: #f8f9fa;
}

.hero-content {
	padding: 2rem;
	position: relative;
	z-index: 2;
	animation: fadeIn 1s ease-out;
}

.image-container {
	position: relative;
	height: 100%;
	border-radius: 20px;
	overflow: hidden;
	box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.25);
}

.hero-image {
	width: 100%;
	height: 100%;
	object-fit: cover;
	transition: transform 0.3s ease;
}

h1 {
	font-size: clamp(2rem, 4vw, 3.5rem);
	margin-bottom: 1.5rem;
	line-height: 1.2;
	color: #2d3436;
}

p {
	font-size: clamp(1rem, 1.5vw, 1.2rem);
	margin-bottom: 2rem;
	color: #636e72;
	max-width: 500px;
	line-height: 1.6;
}

.cta-buttons {
	display: flex;
	gap: 1rem;
	flex-wrap: wrap;
}

.btn {
	padding: 1rem 2rem;
	border: none;
	border-radius: 8px;
	font-weight: 600;
	cursor: pointer;
	transition: all 0.3s ease;
	text-decoration: none;
	display: inline-flex;
	align-items: center;
	gap: 8px;
}

.btn-primary {
	background: #0984e3;
	color: white;
}

.btn-secondary {
	background: #ffffff;
	color: #2d3436;
	border: 2px solid #dfe6e9;
}

.btn:hover {
	transform: translateY(-2px);
	box-shadow: 0 10px 20px -5px rgba(0, 0, 0, 0.1);
}

@media (max-width: 768px) {
	.hero {
		grid-template-columns: 1fr;
		padding: 1rem;
		text-align: center;
	}

	.image-container {
		order: -1;
		height: 400px;
		margin-bottom: 2rem;
	}

	.hero-image {
		height: 400px;
	}

	.cta-buttons {
		justify-content: center;
	}

	p {
		max-width: 100%;
	}
}

@keyframes fadeIn {
	from {
		opacity: 0;
		transform: translateX(-20px);
	}
	to {
		opacity: 1;
		transform: translateX(0);
	}
}
