style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Inter", system-ui, -apple-system, sans-serif;
}

:root {
	--primary: #10b981;
	--secondary: #047857;
	--dark: #111827;
	--light: #f9fafb;
}

body {
	background: var(--light);
	overflow-x: hidden;
}

.hero {
	min-height: 100vh;
	display: grid;
	grid-template-columns: 1fr 1fr;
	align-items: center;
	padding: 120px 5% 60px;
}

.hero-content {
	position: relative;
	z-index: 1;
}

.hero-title {
	font-size: clamp(2.5rem, 5vw, 3.5rem);
	line-height: 1.1;
	color: var(--dark);
	margin-bottom: 1.5rem;
	animation: slideInLeft 1s ease-out;
}

.hero-title span {
	color: var(--primary);
}

.hero-text {
	color: #6b7280;
	line-height: 1.6;
	margin-bottom: 2rem;
	max-width: 500px;
	animation: fadeIn 1s ease-out 0.2s backwards;
}

.cta-button {
	display: inline-flex;
	align-items: center;
	padding: 1rem 2.5rem;
	background: var(--primary);
	color: white;
	border: none;
	border-radius: 0.5rem;
	font-weight: 600;
	text-decoration: none;
	transition: transform 0.3s ease;
	animation: fadeIn 1s ease-out 0.4s backwards;
}

.cta-button:hover {
	transform: translateY(-3px);
}

.stats-grid {
	display: grid;
	grid-template-columns: repeat(3, 1fr);
	gap: 2rem;
	margin-top: 4rem;
	max-width: 600px;
}

.stat-card {
	padding: 1.5rem;
	background: white;
	border-radius: 0.5rem;
	box-shadow: 0 5px 15px rgba(0, 0, 0, 0.05);
	transition: transform 0.3s ease;
}

.stat-card:hover {
	transform: translateY(-5px);
}

.stat-number {
	font-size: 2rem;
	font-weight: 700;
	color: var(--primary);
	margin-bottom: 0.5rem;
}

.hero-image {
	position: relative;
	height: 600px;
	background: url("https://images.unsplash.com/photo-1556761175-5973dc0f32e7?ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=80")
		center/cover;
	border-radius: 1rem;
	box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
	animation: float 6s ease-in-out infinite;
}

@keyframes slideInLeft {
	from {
		opacity: 0;
		transform: translateX(-50px);
	}
	to {
		opacity: 1;
		transform: translateX(0);
	}
}

@keyframes fadeIn {
	from {
		opacity: 0;
	}
	to {
		opacity: 1;
	}
}

@keyframes float {
	0%,
	100% {
		transform: translateY(0);
	}
	50% {
		transform: translateY(-20px);
	}
}

@media (max-width: 1024px) {
	.hero {
		grid-template-columns: 1fr;
		text-align: center;
		gap: 4rem;
	}

	.hero-text {
		margin: 0 auto;
	}

	.stats-grid {
		margin: 2rem auto;
	}

	.hero-image {
		height: 400px;
		order: -1;
	}
}

@media (max-width: 768px) {
	.stats-grid {
		grid-template-columns: 1fr;
	}

	.hero-image {
		height: 300px;
	}
}
