style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
}

.hero {
	height: 100vh;
	background: linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5)),
		url('https://picsum.photos/1920/1080?grayscale');
	background-size: cover;
	background-position: center;
	display: flex;
	align-items: center;
	justify-content: center;
	text-align: center;
	color: white;
	padding: 0 20px;
}

.hero-content {
	max-width: 800px;
}

.hero-content h1 {
	font-size: 3.5rem;
	margin-bottom: 20px;
}

.hero-content p {
	font-size: 1.2rem;
	margin-bottom: 30px;
}

.cta-buttons {
	display: flex;
	gap: 20px;
	justify-content: center;
}

.btn {
	padding: 15px 30px;
	border: none;
	border-radius: 5px;
	font-size: 1rem;
	cursor: pointer;
	transition: transform 0.3s ease;
}

.btn-primary {
	background: #ff6b6b;
	color: white;
}

.btn-secondary {
	background: transparent;
	border: 2px solid white;
	color: white;
}

.btn:hover {
	transform: translateY(-3px);
}

@media (max-width: 768px) {
	.hero-content h1 {
		font-size: 2.5rem;
	}

	.hero-content p {
		font-size: 1rem;
	}

	.cta-buttons {
		flex-direction: column;
	}

	.btn {
		width: 100%;
	}
}

@media (max-width: 480px) {
	.hero-content h1 {
		font-size: 2rem;
	}
}
