style:
/* Base Styles */
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Inter", system-ui, -apple-system, sans-serif;
}

:root {
	--primary: #6366f1;
	--secondary: #4f46e5;
	--dark: #1e293b;
	--light: #f8fafc;
}

body {
	background: var(--light);
}

/* Hero Section */
.hero {
	min-height: 100vh;
	padding: 120px 5% 60px;
	position: relative;
	clip-path: polygon(0 0, 100% 0, 100% 90%, 0 100%);
	background: linear-gradient(45deg, rgba(99, 102, 241, 0.9), rgba(79, 70, 229, 0.9)),
		url("https://picsum.photos/1920/1080") center/cover;
}

.hero-content {
	max-width: 1200px;
	margin: 0 auto;
	text-align: center;
	color: white;
	padding-top: 8rem;
}

.hero-title {
	font-size: clamp(2.5rem, 5vw, 4rem);
	line-height: 1.1;
	margin-bottom: 1.5rem;
	animation: slideUp 1s ease-out;
}

.hero-text {
	font-size: 1.2rem;
	line-height: 1.6;
	margin-bottom: 2.5rem;
	max-width: 600px;
	margin: 0 auto 2.5rem;
	opacity: 0.9;
	animation: fadeIn 1s ease-out 0.2s backwards;
}

.cta-button {
	display: inline-flex;
	align-items: center;
	padding: 1rem 2.5rem;
	background: var(--light);
	color: var(--primary);
	border: none;
	border-radius: 0.5rem;
	font-weight: 600;
	text-decoration: none;
	transition: all 0.3s ease;
	position: relative;
	overflow: hidden;
}

.cta-button::before {
	content: "";
	position: absolute;
	top: 0;
	left: -100%;
	width: 100%;
	height: 100%;
	background: rgba(255, 255, 255, 0.1);
	transition: left 0.4s;
}

.cta-button:hover {
	transform: translateY(-3px);
	box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1);
}

.cta-button:hover::before {
	left: 100%;
}

/* Animations */
@keyframes slideUp {
	from {
		opacity: 0;
		transform: translateY(50px);
	}
	to {
		opacity: 1;
		transform: translateY(0);
	}
}

@keyframes fadeIn {
	from {
		opacity: 0;
	}
	to {
		opacity: 1;
	}
}

/* Responsive Design */
@media (max-width: 768px) {
	.hero {
		clip-path: polygon(0 0, 100% 0, 100% 95%, 0 100%);
	}

	.hero-content {
		padding-top: 6rem;
	}

	.hero-title {
		font-size: 2.5rem;
	}
}
