style:
:root {
    --bg: #fafafa;
    --text: #2d3436;
    --primary: #3b82f6;
    --secondary: #8b5cf6;
    --hologlow: 0 0 15px rgba(59, 130, 246, 0.2);
    --loader-speed: 1.6s;
}
[data-theme="dark"] {
    --bg: #0f172a;
    --text: #e2e8f0;
    --primary: #818cf8;
    --secondary: #c084fc;
    --hologlow: 0 0 25px rgba(129, 140, 248, 0.3);
}
body {
    margin: 0;
    padding: 2rem;
    min-height: 100vh;
    background: var(--bg);
    color: var(--text);
    transition: all 0.4s ease;
    display: grid;
    place-items: center;
    font-family: "Inter", system-ui, sans-serif;
}
.theme-switch {
    position: fixed;
    top: 1rem;
    right: 1rem;
    width: 50px;
    height: 26px;
    background: var(--text);
    border-radius: 13px;
    cursor: pointer;
    transition: background 0.3s;
}
.theme-switch::before {
    content: "";
    position: absolute;
    width: 20px;
    height: 20px;
    background: var(--bg);
    border-radius: 50%;
    top: 3px;
    left: 3px;
    transition: transform 0.3s;
}
[data-theme="dark"] .theme-switch::before {
    transform: translateX(24px);
}
/* Holographic Bars */
.holographic-bars {
    display: flex;
    gap: 6px;
    height: 40px;
}
.holographic-bars div {
    width: 8px;
    background: linear-gradient(to bottom, var(--primary), var(--secondary));
    animation: barWave var(--loader-speed) ease-in-out infinite;
    transform-origin: bottom;
}
@keyframes barWave {
    0%,
    100% {
        transform: scaleY(1);
    }
    50% {
        transform: scaleY(0.3);
    }
}

script:
function toggleTheme() {
    const body = document.body;
    const isDark = body.getAttribute("data-theme") === "dark";
    body.setAttribute("data-theme", isDark ? "" : "dark");
    localStorage.setItem("holographic-theme", isDark ? "light" : "dark");
}
const savedTheme = localStorage.getItem("holographic-theme");
if (savedTheme === "dark") {
    document.body.setAttribute("data-theme", "dark");
}
