style:
:root {
	--bg: #0f172a;
	--text: #f8fafc;
	--primary: #7c3aed;
	--secondary: #475569;
	--transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
}
[data-theme="light"] {
	--bg: #f8fafc;
	--text: #0f172a;
	--primary: #6d28d9;
	--secondary: #cbd5e1;
}
* {
	box-sizing: border-box;
	margin: 0;
	padding: 0;
	transition: var(--transition);
}
body {
	min-height: 100vh;
	display: grid;
	place-items: center;
	background: var(--bg);
	color: var(--text);
	font-family: "Inter", system-ui, sans-serif;
	padding: 2rem;
}
.theme-switch {
	position: fixed;
	top: 2rem;
	right: 2rem;
	width: 4rem;
	height: 2rem;
	border-radius: 2rem;
	background: var(--secondary);
	cursor: pointer;
}
.theme-switch::before {
	content: "";
	position: absolute;
	width: 1.5rem;
	height: 1.5rem;
	border-radius: 50%;
	background: var(--primary);
	top: 0.25rem;
	left: 0.25rem;
	transition: transform 0.3s ease;
}
[data-theme="light"] .theme-switch::before {
	transform: translateX(2rem);
}
.hover-loader {
	width: 120px;
	height: 4px;
	background: var(--secondary);
	border-radius: 2px;
	position: relative;
	overflow: hidden;
}
.hover-progress {
	width: 0;
	height: 100%;
	background: var(--primary);
	animation: progressAnim 2s infinite forwards;
}
@keyframes progressAnim {
	0% {
		width: 0;
	}
	100% {
		width: 100%;
	}
}

script:
function toggleTheme() {
	const currentTheme = document.body.getAttribute("data-theme") || "dark";
	const newTheme = currentTheme === "dark" ? "light" : "dark";
	document.body.setAttribute("data-theme", newTheme);
	localStorage.setItem("theme", newTheme);
}
const savedTheme = localStorage.getItem("theme") || "dark";
document.body.setAttribute("data-theme", savedTheme);
